Insert into SCOTT.TEAM_CHUNGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('한반도지형전망공원','27859','충청북도 진천군 초평면 화산리 563-1',37,128,'약 14.5m의 탑 모양의 전망대를 따라 전망대에 오르면 파란 하늘 아래 다소곳이 자리한 초평저수지와 저수지 물이 마치 삼면이 바다인 한반도를 감싼 청룡처럼 느껴진다. 용의 전설이 꿈틀대는 신비한 물길을 따라 왼쪽 아래를 내려다보면 중부권 최대 규모의 낚시터인 초평저수지에 좌대 낚시터와 함께 빽빽하게 벚꽃으로 가득 찬 꽃섬을 볼 수 있다.<br>','043-539-3622',null,null,null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_CHUNGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('미르숲','27859','충청북도 진천군 초평면 미르숲길 179',37,127,'현대모비스에서 사회공헌활동 일환으로 만들어진 미르숲은 다양한 테마에 따라 식생경관디자인 숲(기원의 숲), 자연상생철학 숲(생각의 숲), 지질역사배움 숲(붉은 바위의 숲), 자연생태동화 숲(요정의 숲), 수변경관투영 숲(거울의 숲), 미래세대문화 숲(약속의 숲). 총 6개의 숲으로 이뤄져 있다. 인공적인 조성을 최대한 지양하고 숲이 고이 간직해 온 소중한 자연 그대로의 모습을 볼 수 있는 곳이다.<br>','043-537-8107 / 043-539-3588',null,null,'숲 거닐기 : 전문 숲 해설가와 함게 숲을 즐기는 진정한 방법을 배울 수 있다.<br />
습지 노릇 : 습지 관찰원에서 이루어지는 초등학생 습지 체험 프로그램<br />
힐링숲피아 : 철학의 숲 일원에서 이루어지는 힐링 체험 프로그램<br />
숲속 착한 여행 : 단체를 대상으로 이루어지는 숲 체험 프로그램',null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_CHUNGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('음성 수정산성','27698','충청북도 음성군 음성읍 약현길38번길 13-13',37,128,'수정산성은 전해오는 기록마다 명칭이 다르게 나타나는데 『신증동국여지승람』에는 ‘고산성’이라 하고, 『대동지지』에는 ‘운성’, 『호서승람』에는 ‘수정산성’이라 하였다.<br /><br />성의 서쪽은 보존상태가 양호하며, 성벽의 높은 곳은 7m가 넘고, 나머지는 대략 2∼3m정도의 높이이며 폭은 4m정도이다. 성벽의 바깥으로 사각형식으로 덧붙여 만든 성벽인 치성은 3곳이 아직 남아있다. 성벽을 따라 돌 수 있게 낸 길인 회곽도가 4∼5m 폭으로 있으며, 성벽의 바깥쪽에는 돌을, 안쪽에는 흙과 잡석을 채워 그것을 한단한단 층을 이루며 쌓아올렸다.<br /><br />성내에 1곳의 건물터가 있으며, 산성의 축성기법이나 유물들로 보아 8∼9세기 초에 축성된 것으로 보인다.<br /><br /><출처 : 문화재청><br>','043-871-3413',null,null,null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_CHUNGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('별천지공원','27912','충청북도 증평군 증평읍 종암길 399',37,128,'2014년 개장한 별천지 공원은 천문대(별)의 이미지와 자연이 살아 숨 쉬는 청정한 이미지(천지)를 합성한 명칭이다. 행복과 장수를 상징하는 거북이가 앉아있는 형상인 좌구산 자락에 자리를 잡았다. 2022년에는 별천지 숲 인성학교가 개관한다. 인성학교에서는 다양한 숲 체험활동과 지역 주민들의 휴식 공간을 마련할 계획이다.<br>공지사항) 별천지 숲 인성학교 건축공사(2019 ~ 2022)','043-835-4581','2014','연중무휴','유아숲체험','만 4~6세','약 100명',null,'상시개방','있음 (약 소형 30대 주차가능)','없음','가능','화장실:있음
주차요금:없음
입 장 료:없음
이용가능시설:잔디광장, 전망대, 거북이와 토끼놀이터, 별천지 유아숲, 하늘정원(풍차), 무궁화동산, 견우직녀길
');
Insert into SCOTT.TEAM_CHUNGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('작은 동산(외솔봉)','27215','충청북도 제천시 청풍면',37,128,'산과 강, 자연이 만들어낸 청풍호의 멋진 풍경을 감상할 수 있는 곳인 작은 동산은 청풍리조트 주차장에서 출발해 외솔봉을 거쳐가는 코스에 위치해 있으며, 작은 동산에 도착하는 데 소요되는 시간은 약 2시간 정도가 걸린다. 좀 더 짧은 코스를 원한다면 외솔봉까지만 오르는 코스를 추천한다. 시원한 청풍호와 병풍 같은 비봉산의 모습이 장관이며, 외솔봉에 도착하기 직전 봉우리에서 가장 멋진 조망이 펼쳐진다.<br>','043-648-0301',null,null,null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_CHUNGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('강선대','29162','충청북도 영동군 양산면 봉곡리 756-1',36,128,'강선대는 충북 영동군 양산면 금강 상류에 자리 잡은 6각형 지붕의 정자이다. 양산 8경이라 하여 양산에는 경치가 아름다운 8곳이 있는데 강선대는 양산 2경으로 꼽히는 곳으로 신선이 내려와서 놀았다는 전설이 전해질 만큼 경관이 뛰어난 곳에 위치해 있다. 우뚝 솟은 바위 위에 자리 잡고 있어 멀리서 보면 주변 노송들과 어우러져 고상한 멋을 풍긴다. 정자 위에 서면 강물이 절벽에 부딪치는 소리와 풍경을 감상할 수 있다. 강선대는 양기가 강한 바위와 음기를 품은 물을 연결해 주는 역할을 하고 있다고 한다. 정자 내부에는 조선시대 시인인 이안눌과 임제의 시가 걸려 있어 풍류를 더한다.','043-740-3205∼7',null,null,null,null,null,null,null,'가능 (대형 4대, 소형 23대, 장애인 3대)','없음','가능',null);
Insert into SCOTT.TEAM_CHUNGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('백야자연휴양림','27682','충청북도 음성군 금왕읍 백야로 461-97',37,128,'백야자연휴양림은 충북 음성군 금왕읍 백야리에 위치한 공립자연휴양림이다. 지정 면적은 37ha이다. 휴양림에는 숙박시설, 오토캠핑장, 수목원, 목재 문화체험장이 갖춰져 있다. 숙박시설은 총 29개소, 캠핑사이트는 26개소로 숲나들 e사이트를 통하여 사전예약 후 이용할 수 있다. 수목원에는 난대식물을 관찰할 수 있는 유리온실, 자라 모양을 닮은 자라암석원, 여러 종류의 장미를 구경할 수 있는 장미동산이 있다. 목재 문화체험장은 관람 및 목공예 체험이 가능한 공간이다.','043-878-2556',null,'목재문화체험장 매주 월요일 휴무','숲해설 프로그램/목재 체험 프로그램',null,null,null,'목재문화체험장(10:00~17:00)','있음(일반 125대, 대형 3대, 장애 8대)','없음','불가','입 장 료:입장료 없음		

이용가능시설:숙박시설, 야영시설, 수목원		

');
Insert into SCOTT.TEAM_CHUNGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('화인산림욕장','29009','충청북도 옥천군 안남면 안남로 151-66',36,128,'끝없이 솟은 메타세쿼이아가 맞아주는 곳, 화인산림욕장은 40여 년 전 이곳 부지를 산 정홍용 대표가 홀로 나무를 심고 가꾸어 온 거대한 숲이다. 치유의 숲, 화인산림욕장에 들어서면 인공시설이 전혀 가미되지 않은 자연 그대로의 모습을 만날 수 있으며, 길이 험하지 않아 가벼운 마음으로 오를 수 있다.  숲 곳곳에 나무가 살아간 흔적이 그대로 남아있고 숲이 주는 상쾌한 공기에 마음까지 시원해지는 기분이 좋아지는 곳이다.<br>','0507-1318-0308',null,null,null,null,null,null,'일출~일몰시까지 (계절에 따라 변동 있을 수 있음)',null,'없음','없음',null);
Insert into SCOTT.TEAM_CHUNGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('홍차가게 소정','29004','충청북도 옥천군 군북면 성왕로 1837',36,128,'탁 트인 창으로 보이는 대청호, 잔잔한 음악, 그리고 향긋한 홍차 한 잔이 눈과 귀, 그리고 입안을 즐겁게하는 홍차가게 소정은 다양한 홍차의 맛과 아늑한 분위기가 매력적인 카페다. 문화공간비 8,000원을 내면 홍차와 디저트 메뉴가 제공되며, 그림 같은 경치를 구경할 수 있다. <br>','043-731-7336',null,null,null,null,null,null,'13:00~18:00 (월, 화 휴무)',null,'없음','없음','시설이용료:문화공간비 8,000원
');
Insert into SCOTT.TEAM_CHUNGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('연암지질생태공원','27901','충청북도 증평군 도안면 노암리 827-1',37,128,'증평 시내에서 자동차로 약 10~15분 거리에 위치한 수변 생태공원이다. 저수지 수변을 따라 약 2km의 데크길이 조성되어 있고, 수변을 가로지르는 출렁다리도 설치되어 있다. 저수지를 둘러싼 야산과 수려한 경관으로 산책하기 좋은 공원이다. 증평군 도안읍 연촌리 일대는 중생대 백악기에 생성된 음성 퇴저군지에 속한다. 이 공원은 약 2km에 걸쳐 변성암, 화성암, 화산암, 퇴적암 등의 다양한 지질구조가 함께 나타나는 곳으로 야외 지질 학습장으로서의 가치가 높은 공원이다. 아이들과 함께하는 가볍게 다녀가기 좋은 곳이다.','증평군청 대표전화 043-835-3114',null,'없음',null,null,null,null,'00:00~24:00','있음','없음','없음','입 장 료:없음

');
Insert into SCOTT.TEAM_CHUNGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('농심테마파크(농업기술센터)','27908','충청북도 증평군 증평읍 내룡길 69-50',37,128,'충북 증평군 농업기술센터가 증평읍 사곡리에 조성한 테마파크다. 화초 9종 5만 포기, 왕벚꽃 등 수목 8종 180그루, 영산홍 등 관목 4종 7천600 그루가 식재되어 주민 휴식 공간 역할을 하고 있다. 특히 꽃양귀비와 수레국화가 만개하는 시기에는 넓은 들판에 활짝 핀 꽃들이 장관을 이룬다. 증평농업기술센터 주차장으로 내비게이션 검색을 하면 찾기 쉽다.','043-838-5959',null,'없음',null,null,null,null,'00:00 ~ 24:00','있음','없음','없음','입 장 료:없음

');
Insert into SCOTT.TEAM_CHUNGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('괴산 트리하우스 가든','28002','충청북도 괴산군 불정면 한불로 1216',37,128,'유럽감성 가득한 충북 여행지 트리하우스 가든은 괴산에 위치하고 있다.<br>충청북도의 민간정원으로 카페와 치유정원 겸 농촌문화 체험농장으로 이용되고 있다.1인 음료를 주문하면 입장이 가능하다.<br>산책하기 좋은 자작나무 숲길도 있으며 숲길 사이에 오두막이 있다. 오두막 안에는 작은 꽃들이 장식되어 있으며 차를 마실 수 있는 공간도 있다.<br>트리 하우스에는 농산물과 화훼도 판매되고 있다.','0507-1356-4460',null,'화, 수 휴무',null,null,null,null,'10:00~19:00','주차가능','없음','없음',null);
Insert into SCOTT.TEAM_CHUNGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('단양 단성벽화마을','27025','충청북도 단양군 단성면 충혼로 16',37,128,'수몰의 아픔을 간직한 채 자리한 단양군 단성면 벽화마을인 단성벽화마을은 꼬불꼬불 골목길을 따라 알록달록 벽화가 펼쳐지는 아름다운 마을이다. 단양8경길, 옛단양길, 퇴계이황길 같은 유교와 전통을 중심으로 한 다양한 벽화가 구성되어있으며, 골목골목마다 화사하고 재기 넘치는 벽화가 가득하다. 또한 구간마다 태양광 LED 조명을 설치해 밤에도 벽화마을의 매력을 감상할 수 있다. 2014년 낡고 침체된 마을을 되살리고자 단양미술협회의 재능기부로 처음 벽화가 그려진 이후 대학생과 미술학도까지 다양한 사람들이 참여해 골목 담벼락마다 벽화로 채워다. 곳곳의 트릭아트 기법을 활용한 벽화, 해와 달을 조형물을 비롯한 포토존이 곳곳에 자리하기 때문에 인생샷을 건질 수 있는 핫플레이스다.<br>',null,null,null,null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_CHUNGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('제천 옥순봉 출렁다리','27219','충청북도 제천시 수산면 옥순봉로 (342)',37,128,'대한민국 명승 제 48호 옥순봉을 가장 가까이에서, 온몸으로 느낄 수 있는 옥순봉 출렁다리는 길이 222m, 너비 1.5m의 연결다리와 함께 944m 길이의 데크로드와 야자매트로 이루어진 트래킹길까지 더해 청풍호와 옥순봉을 둘러볼 수 있도록 조성하였다.','043-641-6738',null,null,null,null,null,null,'동절기(11월~2월) 09:00~17:00
하절기(3월~10월) 09:00~18:00','있음 (제1주차장~제4주차장)',null,null,':
