Insert into SCOTT.TEAM_DAEGU_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('도곡재','42900','대구광역시 달성군 하빈면 육신사길 60',36,128,'조선 정조 2년(1778) 대사성인 서정공 박 문현이 제택으로 건립한 건물이나, 1800년대에 와서 도곡 박 종우의 재실로 사용하면서 그의 호를 따 도곡재라 이름하였다.<br>','053-668-3162',null,null,null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_DAEGU_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('대구 오토바이골목','41921','대구광역시 중구 인교동',36,129,'대구 중구 인교동과 시장북로에 위치한 오토바이 골목은 본래 하천이였으며 양쪽이 대부분 주택지였으나 하천 동편은 2미터 도로였고 하천 서편은 절벽으로 되어 위험성이 있었다. 1953년에 하천 복개(覆蓋, 더러워진 하천에 덮개 구조물을 씌워 겉으로 보이지 않도록 하는 일)로 교통에 편익을 가져오게 되고 대자연 예식장 앞 주유소와 인접되어 있고 옛 수창초등학교 입구 자동차 상가와 가까워 하천 복개한 좌우로 상점이 조성 1961년 인교동 154번지 서울 오토바이상회를 효시로 점차 확산되어 현재 점포수 40여 곳, 약 200미터 이상 오토바이 전시장을 방불케 하는 대구의 이색거리로 자리 잡고 있다. 신제품과 중고, 국산과 외제, 부품 등 오토바이에 관한 모든 것을 오토바이 골목에서 구입할 수 있다.','대구관광정보센터 053-627-1337',null,null,null,null,null,null,null,null,'없음','없음',null);
