Insert into SCOTT.TEAM_DAEGU_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('원호루','42988','대구광역시 달성군 현풍면 현풍동로 92',36,128,'이 누각은 구한말 1897년 현풍현감 홍 필주가 관직에 있을 때 인근 현령 미향유들과 정사를 논의하고, 풍류와 여흥을 즐기기 위하여 지은 것이다.원래 지금의 현풍면사무소(부리 352) 뒤의 동산에 있었으나 1989년에 면사무소 증축으로 해체되었다가 초대민선 달성군수 양시영에 의해서 현 위치에 이축 복원되었다.(1996∼1998)<br>','053-668-3162',null,null,null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_DAEGU_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('남평문씨본리세거지','42967','대구광역시 달성군 화원읍 인흥3길 16',36,129,'이곳은 원래 절이 있었던 명당 터를 남평문씨 일족이 세거를 위하여 정전법에 따라 구획을 정리하여 터전과 도로를 반듯하게 열었고 집을 지었던 곳이다. 지금은 세거지에 아홉 채의 집과 정자 두 채가 있으며 주거지의 도로에 면한 부분에는 나지막한 담을 쌓았다. 수봉정사는 이곳의 대표적인 건물로 새거터 전면에 위치하고 있으며 찾아오는 손님을 맞기도 하고, 일족의 모임도 열던 큰 건물이며 정원의 꾸밈이 아름답다. 광거당은 자제들의 학문과 교양을 쌓던 수학 장소이며, 인수문고는 1만여권의 도서와 문중의 보물을 보존하던 건물이다. 처음에는 소규모이던 인수문고는 서고를 후에 크게 지었고, 그 이웃에 도서의 열람을 위한 건물도 배설하였다.<br>','053-631-8686',null,null,null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_DAEGU_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('수봉정사','42967','대구광역시 달성군 화원읍 인흥3길 16',36,129,'정자인 수봉정사는 남평문씨 본리세거지의 대표적인 건물로 세거지 입구에 위치하고 있어 찾아오는 손님들을 맞기도 하고 일족의 모임을 열었던 건물로서 정원의 꾸밈이 아름답다. 수봉정사는 1936년 일제때 문 영박(文永樸 : 1880∼1930)의 인격과 학식을 기리고 후손들의 학문과 교양을 쌓기 위한 교육 장소로서 자제들이 건립한 것인데 자는 장지(章之), 호는 수봉(壽峰), 본관은 남평(南平)이다.<br>','053-668-3162',null,null,null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_DAEGU_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('인흥마을','42967','대구광역시 달성군 화원읍 인흥3길 16',36,129,'남평문씨의 중시조로 알려진 삼우당(三憂堂) 문익점(文益漸)의 후손이 대구에 입향한 것은 대체로 500년 전으로 전해지고 있으며, 이곳 인흥에 자리를 잡은 것은 그의 18세손인 인산재(仁山齋) 문경호(文敬鎬, 1812~1874) 때이다. 그는 문씨만의 마을을 만들 것을 계획하고 원래 고려의 대찰(大刹)인 인흥사가 있었던 자리에 터를 잡아 오늘의 인흥마을을 새로 열었다고 한다.세거지에 최초로 건립한 건물은 1820년을 전후해서 지은 재실 용호재(龍湖齋)로 현재 광거당이 있는 곳이다. 저택이 들어선 것은 1800년대 후반으로 처음에는 초가로 시작했으나 그후 100년에 걸쳐 지금의 세거지가 형성되었다. 현재 70여채의 기와집이 한울 안에 정연히 들어서 있는 이마을은 건축연대가 200년 미만이지만 전통적인 영남지방 양반 가옥의 틀을 지키고 있으며, 세거지 구성과 주위 경관의 조화는 어느 곳에서도 흔히 볼 수 없는 마을이라 하겠다.<br>','053-637-5416',null,null,null,null,null,null,null,null,'없음','없음',null);
