Insert into SCOTT.TEAM_DAEGU_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('중리동 곱창골목','41843','대구광역시 서구 평리로35길 28-10(중리동)',36,129,'대구에는 안지랑만큼 유명한 중리동이 있다. 모두 대구가 품은 곱창 골목이다. 안지랑이 돼지 곱창과 막창이 주력이라면 서구의 중리동은 소곱창이 주력 메뉴인 것이 다른점이다. 중리동 곱창골목의 역사는 30여 년 전으로 거슬러 올라간다. 지금의 아울렛 자리에 도축장이 들어서면서 주변에 식육식당과 부속물을 파는 포장마차가 생긴게 시작이었다. 2001년 검단동으로 도축장은 옮겨갔지만 곱창 전문점들은 여전히 자리를 지킨다. 소 대창과 곱창은 물론 주먹시 같은 특수 부위까지 맛볼 수 있는 맛골목이다.<br>','053-252-2696',null,null,null,null,null,null,null,null,'불가','불가','화장실:식당 내 화장실 이용
');
Insert into SCOTT.TEAM_DAEGU_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('낙동서원','42804','대구광역시 달서구 송현로7길 32',36,129,'낙동서원 (洛東書院)은 본래 이 인근에는 우배선(禹拜善)을 제향하기 위하여 1708년(숙종34)에 건립되었던 덕동서원이 있었는데 대원군의 서원 철폐령으로 훼철된 후 건물도 없어졌다. 현재 낙동서원은 최근 1965년 단양우씨 판서공과 종인의 협조와 17대손인 종식, 종묵 형제가 상인동 885번지에 사비로 건립하여 향내 유림에 헌납한 것이다. 이 서원에는 임진왜란 당시 의병장으로 크게 활약하였던 우배선과 우탁(禹倬), 신현(申賢), 우길생(禹吉生), 우현보(禹玄寶) 5분을 모시고 향사를 매년 음력 3월에 봉행하고 있다.','053-668-2000',null,null,null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_DAEGU_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('용강서원','42605','대구광역시 달서구 선원로33길 101',36,129,'용강서원(龍岡書院)은 허득량(許得良)과 허복량(許復良)을 모신 재실로 1639년 용강서원으로 건립하였으나 1868년(고종5) 흥선대원군의 서원철폐령으로 훼철되었다. 1920년 중창하여 용강사(龍岡祠)라 부르고 1986년에 복원하여 위 허득량(許得良)과 허복량(許復良) 양위(兩位)를 배향하고, 9대조인 고려문하시중(高麗門下侍中) 시충목(諡忠穆) 허유전(許有全)을 정위(正位)에 추배하였다.','053-581-0144',null,null,null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_DAEGU_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('동인동 찜갈비 골목','41908','대구광역시 중구 동덕로36길 9-12(동인동1가)',36,129,'70년대부터 중구 동인동 주택가에서 찌그러진 양은 냄비에 푹 익힌 소갈비를 담아 매운 고춧 가루와 다진 마늘을 주 양념으로 비벼먹는 동인동 찜갈비는 간장으로만 맛을 내는 갈비찜과는 판이하게 다른 대구에만 있는 스타일이다. 술안주로도 알맞으며 식사 후 남은 양념에 밥을 비벼 먹는 맛이 맵싸하고 화끈한 것이 특징이다. 서울.경기권의 갈비찜과는 달리 혀가 얼얼할 정도로 매우면서도 달짝지근한 맛이 일품이다.','대표 번호 없음 (업체별 상이)',null,'업체별 상이',null,null,null,null,'업체별 상이','있음 (업체별 상이)','없음','없음',null);
