Insert into SCOTT.TEAM_GYEONGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('모포항','37943','경상북도 포항시 남구 장기면 모포리 61-5',36,130,'모포항은 낚시를 하거나 사진을 찍는 사람들에게 인기가 많은 곳이다. 작고 아담한 항구지만 갑오징어, 광어, 가자미, 고등어, 볼락, 참돔 등 계절 따라 잡히는 어종이 다양하다 보니 낚시객들 사이에 유명해졌고 일몰과 일출, 바다 사진을 찍기 위해 출사 나오는 사진작가들 사이에도 이미 소문난 곳이 됐다. 모포항에서 북쪽 방면으로 이동하면 구룡포항, 구룡포 과메기 물회 거리, 호미곶 해맞이 광장이 있어 낚시뿐 아니라 연계 관광에 나서기도 좋다.','054-270-8282',null,null,null,null,null,null,'제한 없음','주차가능','없음','가능',null);
Insert into SCOTT.TEAM_GYEONGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('신창 간이 해수욕장','37946','경상북도 포항시 남구 장기면 신창리',36,130,'신창 간이해수욕장은 이름 그대로 여름철에만 일시적으로 개장했다 폐장하는 간이해수욕장이다. 일반 해수욕장에 비해 아담하지만, 해변이 길어 산책만 하기에도 좋다. 백사장의 모래는 곱고 해수욕장의 수심은 얕아 남녀노소 안전하게 물놀이를 즐길 수 있다. 민물과 바닷물이 만나는 신창 간이해수욕장 주변에는 신창 2리 어촌체험마을을 비롯해 음식점, 숙박업소 등이 자리 잡고 있다. 해수욕장을 기점으로 남쪽으로 내려가면 경주의 유명 관광지가 즐비하고, 북쪽으로 올라가면 포항의 여러 해변이 펼쳐져 연계 관광이 용이하다.','054-270-8282',null,null,null,null,null,null,null,'주차가능','없음','가능','화장실:가능(남녀 구분)
');
Insert into SCOTT.TEAM_GYEONGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('용한 1리 해수욕장','37556','경상북도 포항시 북구 흥해읍 영일만항로 33',36,129,'용한 1리 해수욕장은 영일만항과 맞닿은 곳에 펼쳐져 있다. 영일만항에는 낚시 관광객이 많다면 용한 1리 해수욕장에는 물놀이를 즐기는 가족단위 여행객들이 많다. 백사장 길이는 짧지만 물이 얕고 파도가 거칠지 않아 아이들도 안전하게 즐기기 좋다. 바다색이 아름답기로 유명한 이곳은 최근 서핑의 성지가 되면서 서핑 강습이나 각종 물놀이 용품 대여가 불편 없이 이뤄지고 있다. 전용 주차장이 없지만, 영일만항 주차장에 주차하고 걸어도 5분이 채 걸리지 않는다. 해수욕장 길 건너편에는 다양한 음식점들이 성업 중이다.','054-270-8282',null,null,null,null,null,null,'제한 없음',null,'없음','가능',null);
Insert into SCOTT.TEAM_GYEONGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('구산항','36354','경상북도 울진군 기성면 구산봉산로 126-4',37,129,'구산항은 1991년 국가 어항으로 지정됐다. 낚시객들은 649m의 북방파제, 567m의 남방파제, 240m의 방사제 등에서 낚시를 즐긴다. 항과 더불어 인근에서 잡히는 어종은 문어, 오징어, 가자미 등이다. 낚시 어선을 타고 먼바다로 나가 낚시를 즐길 수도 있다.<br>구산항 인근에는 금빛 모래와 소나무 숲으로 이뤄진 구산해수욕장, 투명카누와 갯바위 낚시 체험이 가능한 구산어촌체험마을 등이 있어 연계 관광이 용이하다.<br>울진군은 2021년 7월 현재 관광 활성화를 위해 기산항에 해상 스카이워크와 전망대 등의 조성 계획을 검토 중이다.','054-789-6864',null,null,'구산어촌체험마을 투명카누체험 등',null,null,null,null,'있음','없음','불가','입 장 료:무료
');
Insert into SCOTT.TEAM_GYEONGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('기성항','36348','경상북도 울진군 기성면 기성본길 117-4',37,129,'기성항은 1984년 지방 어항으로 지정됐다. 이곳은 관광객 가운데 낚시를 즐기는 사람들이 많이 찾는다. 낚시객들은 주로 430m가량의 방파제 이곳저곳에서 낚시를 한다. 기성항 방파제에선 감성돔과 벵에돔이 많이 잡힌다고 알려졌다. 출조하는 어선을 타고 먼바다로 나가면 가자미, 숭어, 양미리 등도 낚을 수 있다.<br>기성항에서 북쪽으로 방향을 잡고 이동하면 기성망양해수욕장이 있고, 남쪽으로 내려가면 구산해수욕장이 있어 연계 관광이 쉽다.','054-789-6864',null,null,null,null,null,null,null,'있음','없음','불가','이용가능시설:방파제
');
Insert into SCOTT.TEAM_GYEONGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('울진항','36321','경상북도 울진군 울진읍 현내항길 258',37,129,'울진항은 소규모 어항인 어촌정주어항으로 과거 명칭은 현내항이었다. 이곳은 2021년 우수 귀어귀촌인 및 어(漁)울림마을 선정평가에서 최우수상을 수상했다. 원주민과 귀어인의 화합이 돋보이는 이곳은 낚시객들의 방문이 잦다. 울진항 갯바위나 방파제에서 미끼를 던지면 감성돔이 잡히기 때문이다. 고즈넉한 분위기라서 울진 여행길에 잠시 들러 풍경을 감상하는 관광객도 꽤 있다.<br>울진항은 여러 음식점과 숙박업소가 성업 중인 울진 시내와 가까운 거리에 자리 잡고 있다. 게다가 봉평해수욕장과 망양정해수욕장까지 이동하기 수월해 연계 관광이 가능하다.','054-789-6864','상시개방',null,null,null,null,null,null,'있음','없음','불가','입 장 료:무료
');
Insert into SCOTT.TEAM_GYEONGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('(구)사곡중학교 메밀꽃밭(허수아비공원)',null,'경상북도 의성군 사곡면 의성사곡로 993',36,129,'옛 사곡중학교 터에 건물은 모두 허물고 운동장에 메밀꽃 꽃밭이 조성되어 있다. 메밀꽃 뿐만 아니라 한쪽 편에는 칸나가 있고, 다른 편에는 코스모스가 피어있다. 군데군데에는 시골에서만 볼 수 있는 허수아비들이 자리를 지키고 있다. 바람개비들도 줄지어 세워져있어 포토존으로 이용할 수 있다. 안쪽에는 마늘로 된 돼지모형도 있다.','1588-1369',null,null,null,null,null,null,null,null,'없음','없음',null);
