Insert into SCOTT.TEAM_GYEONGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('소암서당','39914','경상북도 칠곡군 기산면 지산로 807-11(기산면)',36,128,'기산면 평복리에 있는 소암 서원은 채몽연(蔡夢硯), 채무(蔡楙) 부자와 장내범(張乃範)을 모신 서원이다. 1703년(숙종 29) 10월 유학자인 장벽, 장세휘, 박정석 등 20여명이 소암동 팜암바위 뒷산에 사당과 강당을 지어 소암사(嘯巖祠)라 하고 1712년 (숙종 38) 11월 2일 채몽연(蔡夢硯), 채무(蔡楙)부자를 봉안하였다. 1803년(순조 3)에 서원으로 승격하고 1847년(헌종 13)에는 장내범(張乃範)을 배향하였다. 1871년(고종 8)에 서원철폐령에 의하여 모두 훼철되었으나, 1932년 3월 강당만 복원하면서 소암서당이라 하여 현재에 이르고 있다.<br>','054-979-6452',null,null,null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_GYEONGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('송곡서원','38867','경상북도 영천시 청통면 애련길 174',36,129,'1702년(숙종 28)에 지방유림의 공의로 유방선(柳方善)과 곽순(郭珣)의 학문과 덕행을 추모하기 위하여 묘우(廟宇) 경현원(景賢院)을 창건하여 위패를 모셨다. 그 뒤 원호를 ‘송곡’으로 고치고 1712년(숙종 38) 이현보(李賢輔) · 심지원(沈之源)을 모시고, 그 뒤 이보흠(李甫欽)과 윤봉오(尹鳳五)를 추가로 배향하였다. 선현배향과 지방교육의 일익을 담당하여 오던 중, 대원군의 서원철폐령으로 1868년(고종 5)에 훼철되었으며, 1965년 유림의 공의로 복원하여 유방선과 이보흠만 배향하였다.<br>','054-330-6354',null,null,null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_GYEONGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('신천서원','36830','경상북도 예천군 예천읍 왕신1리길 151',37,128,'1985년 8월 5일 경상북도문화재자료로 지정되었다. 의성김씨문중 소유로 문중에서 관리한다. 조선 초기의 공신 맹암 김영열(金英烈)의 뜻을 받들고 후손을 교육하기 위한 학원으로 1945년에 설립하였다. 김영열은 1400년(정종 2) 왕위계승을 둘러싼 왕자의 난인 방간의 난을 평정하고 익대좌명공신으로 책록 되었다. 이 서원은 전라남도 장성군 장성읍 옥천동에 학계서원을 재건할 때 함께 세운 것이다.<br>','054-650-6395',null,'연중개방',null,'전연령',null,null,null,'주차가능','없음','없음','입 장 료:무료
');
Insert into SCOTT.TEAM_GYEONGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('암포서원','40038','경상북도 성주군 월항면 안포길 89(월항면)',36,128,'본래 선조를 제사하기 위한 사당과 교육을 겸한 서당으로 출발하였으나, 1569년 (선조 2) 국학의 제도에 따라 선현을 제사하고 유생들을 교육하면서 건물의 중건과 함께 그 명칭을 서원으로 변경하였다. 1871년(고종 8) 음력 3월 20일 서원철폐령(書院撤廢令)으로 전국에 사액서원 47개소만 남기고 통폐합할 때 훼철되었다. 1960년5월29일 정면4칸 측면2칸의 한옥으로 복원하였으며, 복원된 건물이 퇴락하여 사당 1동은 문화재적 가치보존을 위해 존치하기로 하고, 그 외 건물은 경내에 이전 중건하였으며, 2007년5월13일 성주향교 유림 및 각 문중대표 주관으로 낙성고유식을 거행하였다. 건물의 공간적인 구성은 풍수지리학적 지형지세에 따라 서원의 우측으로 사당을 배치하였으며, 서원(書院)ㆍ사당(祠堂)ㆍ단목(壇木)ㆍ단정(壇井) 등이 있다.<br>','054-930-6795',null,null,'향사제(매년 9월15일)',null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_GYEONGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('연계서원','38802','경상북도 영천시 신녕면 연정큰길 67',36,129,'연계서원은 2004년 봄에 세워졌다. 본래 이 자리는 한덕련이 군위군 고로면과 산성면 영천군 임고면 매호리 등 여러 곳을 전전하다 마지막으로 정착하여 후진양성을 계속하던 심락서당 자리다. 문인과 후손들로 주축이 되어 1995년 6월 ‘송계선생추모사업회’를 구성하여 영천문화원에서 창립총회를 가졌으며, 그 후 회원들의 성금 등으로 모금 된 사업회 자금 6억5천 만원과 국비 1억5천 만원 도합 8억원으로 영천시 신녕면 연계리 옛 서당터에 서원을 짓고, 2004년 5월 준공식을 가졌다.<br>','054-336-1148',null,null,null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_GYEONGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('연악서원','37228','경상북도 상주시 지천1길 49',36,128,'갑장(甲長)의 산이 힘차게 동쪽으로 뻗어나와 좌우로 펼쳐져 한 구역을 이루니 이를 연악(淵嶽)이라고도 한다. 열악(列嶽)이 서북으로 둘러있고 대천(大川)이 동남으로 감아돌아 수석(水石)이 맑고 기이하며 계류구곡의 탁영담(濯纓潭)과 영귀암(詠歸巖)이 이 산에 좋은 경치이다.(淵嶽書院重建記 中)산명으로 서원에 이름을 한 중심적인 서원으로 상산지(尙山誌)에는 주남(州南) 양산리에 있으니 1553년(명종 8)에 목사 신잠(申潛)이 상주에 세운 서당 17개소 중의하나이며 직접 친히 연암서원이란 네 글자를 크게 현판 하였는데 임진왜란 때 서당과 현판이 모두 불타 버렸다고 한다. 그 후 1601년 (선조 34)과 1658년(효종 9)에도 중건하여 1702년(숙종 28)에 서당에서 서원으로 승격되었다. 이때 박언성(朴彦誠), 김언건(金彦健), 강응철(康應哲)을 배향하였으며 1726년(영조 2)에 김각(金覺), 조광벽(趙光壁), 강용량(康用良)을 추배하였다. 1868년(고종 5)에 서원철폐령으로 훼철되었으며, 1913년 단소(壇所)를 설립하고 1974년 강당을 중건하였으며, 1986년에는 사당을 복원 고유하였다.<br>','054-537-6095',null,null,null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_GYEONGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('오산서원','36134','경상북도 영주시 대동로103번길 31-32(가흥동)',37,129,'순조 34년(1834) 오산서원을 창건 하고 김난상의 위패를 오천서원(梧川書院)에서 갈라 나와 봉안하고 묘호를 경현사(景賢祠) 강당을 명교당(明敎堂)이라 하였다. 기문(記文)은 이휘녕(李彙寧)이 짓고 봉안문(奉安文)은 유태좌(柳台左)가 짓고 상향문(常享文)은 이광정(李光靖)이 짓고 서원 승격할때 고유문(告由文) 김희주(金熙周)가 지었다. 지금은 서원자리에 김난상의 정자인 매양정과 김난상의 신도비, 사당이 세워져 있으며 사당안에는 오산이사, 경현사, 오산서원 현판이 보관되어 있다.<br>','054-639-6581',null,null,null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_GYEONGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('오암서원','40066','경상북도 성주군 수륜면 참별로 1',36,128,'조선 중기의 문신이자 성리학자인 한강 정구(鄭逑 1543∼1620)의 문인이었던 죽헌 최항경(崔恒慶 1560~1638)과 그의 두 아들 최은, 최린 등 삼부자를 배향한 서원이다. 최항경은 1605년 사마시에 합격하였으나 벼슬에 뜻을 두지 않고 오암정사(鰲巖精舍)를 지어 학문에 힘썼다 한다.  1730년 운암서원(雲巖書院)을 지어 최항경의 제사를 지냈으나 1741년 서원철폐령으로 사라졌고, 1781년 다시 오암서원을 지어 배향했으나 이 또한 1871년 대원군의 서원철폐령으로 숭덕재사(崇德齋舍)만 남고 철거되었다. 1985년 후손들이 대강당과 광영루(光影樓)를 지었고, 2007년 효덕사(孝德祠)를 신축하고 대강당과 숭덕재사를 개축하여 오암서원을 복원했다.<br>','054-930-8371',null,null,null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_GYEONGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('옥동서원','37274','경상북도 상주시 모동면 수봉2길 29',36,128,'옥동서원은 1518년(중종 13)에 황희(黃喜)의 학문과 덕행을 추모하기 위해 백화서원(白華書院)을 건립하면서 창건되었다. 1580년(선조 13)에 영당(影堂)을 건립하여 향사(享祀)를 지내면서 서원의 면모를 갖추게 되었다. 1714년(숙종 40)에 전식(全湜)을 배향하였고, 1715년(숙종 41)에 현재의 위치로 이건(移建)하였다.','054-537-6095',null,null,'학생 인성교육(소학, 명심보감, 예절)',null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_GYEONGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('묘각사(영천)','38814','경상북도 영천시 자양면 용화길 499',36,129,'경상북도 영천시 자양면 용화리 산9번지 기룡산(騎龍山)에 자리한 대한불교조계종 제10교구 본사 은해사의 말사이다. 기룡산(騎龍山) 묘각사는 지금으로부터 1300여년 전 신라시대 선덕여왕 당시 의상스님이 창건한 것으로 전해지는 유서 깊은 사찰로, 영천시 자양면 용화리에 자리하고 있다. 1999년부터 대대적인 불사에 들어가 아미타삼성전을 복원하고 관세음보살상도 새로 제작하였다. 이 절의 부근은 예로부터 불보성지(佛寶聖地)로 널리 알려졌다. 그래서인가 절의 뒷산은 보현보살이 머무른다는 보현산(普賢山)이며, 산 아래 동네는 마을입구부터 미륵불(彌勒佛)의 상주처(常住處)임을 암시하듯 미륵불의 용화삼회법회의 의미를 상징하는 용화동(龍華洞)이다. 이어 삼매동(三昧洞) 덕연동 선원동(禪院洞) 원각동(圓覺洞) 공덕동(功德洞) 정각동(正覺洞) 등 수많은 지명이 마치 화장세계(華藏世界) 불국정토를 표현한 듯한 마을 이름들이 많다. 이렇듯 10만억 국토를 지닌 서방정토는 이곳 기룡산에 유연찰토를 정하며 바로 이 사바에 시현되고 있다.<br>','054-336-6633',null,'연중무휴',null,null,null,null,'일출~일몰','주차 가능','불가','없음','화장실:있음
