Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('신읍리 동지화 마을','24113','강원도 화천군 화천읍 신읍리',38,128,'화천군 화천읍 신읍리에 자리한 동지화마을은 동짓날에도 배꽃이 피는 따뜻한 마을이라는 뜻으로 ''동지화 마을''이란 이름이 생겨났다. 화천군에서 약초 재배를 기획적으로 시범 조성한 마을로, 와룡담, 용수, 산 약초 재배, 물푸레나무 등이 대표 관광요소라 할 수 있다. 동 지호부터 사구까지 자연마을이 약 16km에 걸쳐 어우러져 있으며, 용신, 동지화, 신촌, 남벌원, 율대, 솔골 등이 있다.<br>','033-440-2225',null,null,null,null,null,null,'상시이용가능',null,'없음','없음',null);
Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('유촌리 파로호 느릅마을','24138','강원도 화천군 간동면 파로호로 923-15',38,128,'파로호 느릅마을은 강원도 화천군 간동면 유촌리로 자연을 소중히 여기며 인간 본연의 내면성을 그대로 간직하고 있는 자연마을이다. 자연경관을 태고의 본 모습으로 이어 가고자 온 주민이 노력하고 친환경 농산물을 생산하여 소비자에게 보다 믿을 수 있는 신의를 중요시하고 내 이웃을 사랑하는 마을이다.<br>','033-442-5517',null,null,null,null,null,null,'상시이용가능',null,'없음','없음',null);
Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('643고지전투전적비','24132','강원도 화천군 화천읍 평화로 705-6',38,128,'643고지 전투 전적비는 평화의 댐으로 가는 길에 있다. 한국전쟁 당시 수리봉 643고지를 중공군 제20군과 싸웠던 국군 제6사단과 미군 17연대의 전공을 기념하기 위해 세운 것이 바로 643고지 전투 전적비다. 백병전 끝에 적을 물리치고 사수한 전과를 길이 남기고 이 전투에서 산화한 장병들을 추모하기 위해 건립했다.<br>','관광안내소 033-440-2575',null,null,null,null,null,null,'상시이용가능',null,'없음','없음',null);
Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('곡운구곡 (강원평화지역 국가지질공원)','24161','강원도 화천군 사내면 용담리',38,128,'곡운구곡(谷雲九曲)은 북한강의 지류하천인 지촌천의 일부구간에 해당된다. 이 곳은 단단한 화강암으로 이루어진 강바닥을 따라 다채로운 하천지형이 발달하고 있으며, 선캠브리아기 변성암의 습곡 및 단층구조를 관찰할 수 있는 중요한 지질명소에 해당된다.곡운구곡은 조선시대의 성리학자인 김수증(金壽增, 1624~1701)의 호 ‘곡운’을 딴 것으로, 그가 1670년부터 화천군 사내면 영당동에 거주하며너 지촌천의 물굽이 9개에 각각 이름[방화계(榜花溪), 청옥협(靑玉峽), 신녀협(神女峽), 백운담(白雲), 명옥뢰(鳴玉瀨), 와룡담(臥龍潭), 명월계(明月溪), 융의연(隆義淵), 첩석대(疊石坮)]을 지어 곡운구곡이라 칭한 데서 유래하고 있다.<br /><br />이는 속세를 떠나심산유곡으로 몰입해 은둔과 안닉을 통해 학문을 정진하고자 하는 성리학의 이상을 구현하기 위한 것이다.곡운구곡 일대는 하천의 규모가 작은 편에 속하고 일부 남아있던 지형이 도로 개설 과정에서 소실되었지만 전체적으로 화강암을 기반으로 한 수려한 암석 경관, 청정함 등으로 인해 구곡문화의 측면뿐만 아니라 지형경관지원 측면에서도 손색이 없는 우수한 자원으로 손꼽히고 있다. 전체 9곡 중에서 가장 경관이 뛰어난 곳은 화강암 지대에 위치한 제3곡인 신녀협과 제4곡인 백운담이다. 한편, 제1곡과 제3곡 사이의 변성암(호상편마암)지대에서는 변성작용 중에 일어난 습곡과 단층 구조를 관찰할 수 있다.<br /><br /><출처 : 강원평화지역 국가지질공원><br>','033-249-3881',null,null,null,null,null,null,'상시이용가능',null,'없음','없음',null);
Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('대성산지구 전적비','24104','강원 화천군 상서면 다목리 산1-1',38,128,'6.25 전쟁 때 일어났던 대성산지구 전투 장병들의 영웅적인 전공을 높이 찬양하고 조국 수호를 위해 목숨을 바친 호국영령들의 넋을 추모하면서 그 위훈을 자손만대에 길이 전하고자 세운 비(碑). 1983년 10월 1일에 화천군에 있는 육군 제8305부대에서 건립했으며, 보병 제15사단(6685부대)에서 관리하고 있다.대성산지구 전투는 1951년 6월 9일 국군 2사단 2, 3대대가 1041고지에서 활동 중인 중공군 제20군 58사단 177연대의 적군 병력을 섬멸하기 위해 벌였던 전투다. 당시 국군은 선제공격을 개시해 1041고지와 신월동 및 865고지를 탈취했다.<br>','화천 관광안내소 033-440-2575',null,'연중무휴',null,null,null,null,'09:00 ~ 18:00',null,'없음','없음',null);
Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('피니시타워','24140','강원도 화천군 하남면 춘화로 3331-50',38,128,'피니시타워가 화천의 새로운 명소로 부상하고 있다. 높이 20m, 폭 6m, 길이 90m의 대형 철골 조형물로, 독특한 외관이 가장 먼저 눈길을 사로잡는다. 피니시타워는 ‘2007년 아시아 카누경기대회’ 당시 피니시라인의 기록계측 및 중계를 위해 설치된 건축물이다. 대회 폐막 후 기존 시설을 개조해 수상골프장, 전망대 휴게실 등을 갖춘 관광 명소로 이용하고 있다. 조정, 카누 등의 국내 체육대회를 유치하는 것은 물론 ‘화천 쪽배 축제’ 개최 장소 등으로 활용하고 있다.','033-440-2251',null,null,null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('화천조경철천문대','24150','강원도 화천군 사내면 천문대길 431',38,127,'조경철천문대의 명칭은 ''광덕산 천문대''다.   별과 함께 살아온 아폴로박사 고성 조경철 박사님의 업적을 기리기 위해 2014년 10월 10일 화천군의 주도로 건립되었으며, 이 곳 조경철천문대에서는 대중과 친근한 과학자로, 과학의 대중화를 꾀하고, 우주과학 입국을 위한 계몽자로써 큰 공헌을 하신 조경철 박사님의 꿈과 발자취를 고스란히 느껴볼 수 있는 곳이다. 천문대에 마련된 ''조경철 기념관''에는 박사의 열정 가득한 삶의 발자취를 엿볼 수 있다. 전시공간에는 천문전시실이 있어 우주중력체중계에 올라가 볼 수 있고 별자리 스템프도 찍을 수 있다. 무엇보다 천문대의 스케일 큰 밤하늘 보기는 잊지 못할 추억이 될 것이다.','033-818-1929',null,'매주 월요일(공휴일인 경우 화요일), 1월 1일, 설/추석 당일',null,null,null,null,'14:00~22:00',null,'없음','없음',null);
Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('병풍산','24135','강원도 화천군 간동면 방천리',38,128,'화천군 간동면 도송리와 오음리에 걸쳐 있는 병풍산(796m)은 ‘고진감래(苦盡甘來)’라는 옛말을 체험하게 만드는 산이다. 처음 산 오름길이 비교적 험하고 힘들지만 일단 능선에 오르고부터는 자연이 만들어낸 귀한 선물을 받게 된다. 병풍산은 하루코스 산행지로 안성맞춤으로 산 맨 아래에서 출발해도 넉넉히 잡고 3시간이면 정상에 오를 수 있으며 하산까지 4시간이면 충분하다.<br>','033-440-2575',null,null,null,null,null,null,'상시이용가능',null,'없음','없음',null);
