');
Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('딴산유원지','24132','강원도 화천군 화천읍 대이리 산1-1',38,128,'화천 읍내에서 평화의 댐 쪽으로 4km 정도 가면 딴산 유원지가 있다. 딴산은 산이라기보다는 물가에 자리하는 조그만 동산이다. 마치 섬처럼 두둥실 떠 있는 모습이 인상적이다. 앞으로 잔잔한 풍산천이 흐르고 있어, 낚시나 물놀이가 가능하고, 피크닉이나 캠핑을 하려 찾는 사람들이 많다. 인공 폭포가 있어 주말이나 휴가철에는 시원한 폭포를 감상할 수 있다.','033-440-2547',null,'연중무휴',null,null,null,null,'00:00 ~ 24:00','가능 (50대 이상 가능)','없음','없음','이용가능시설:매점
화장실:가능 (남여화장실)
');
Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('파로호전망대','24132','강원도 화천군 간동면 파로호로 208',38,128,'파로호의 그림 같은 절경을 감상할 수 있는 전망대이다. 파로호 안보전시관 뒤쪽 동산으로 150m 정도 올라가면 자리 잡고 있다. 호수가 내려다보이는 전망대 난간을 비롯 정자와 벤치가 마련되어 있다. 6.25 한국전쟁 당시 용맹스러운 국군을 기리기 위해 육군 칠성부대, 한국전력, 화천군이 함께 건립하였다.','033-440-2544',null,'연중무휴',null,null,null,null,'00:00 ~ 24:00','주차가능','없음','없음',null);
Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('연꽃단지','24147','강원도 화천군 하남면 건넌들길 97',38,128,'화천군 하남면 서오지리에 위치한 연꽃 생태공원으로 온 가족이 함께 즐길 수 있는 공간이다. 건넌들이라고 불리던 늪지대에 2005년부터 조성하기 시작했으며 수련, 백련, 순채, 가시연, 어리연꽃 등 400여 종에 달하는 연꽃들이 계절에 따라 꽃을 피워 사진작가들과 여행객들에게 인기 있는 관광명소다. 원래 이곳은 쓰레기와 오물이 넘쳐나는 오염지대였으나 주민들이 힘을 합쳐 생태계를 살려 나가기 위해 연을 심기 시작했다고 한다. 덕분에 이곳을 떠났던 물고기들이 돌아와 수중 생태환경도 되살아나고 있다.','033-440-2952 / 033-440-2575',null,'연중무휴',null,null,null,null,'상시 개방','주차가능','없음','없음',null);
