Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('잔교해변','25047','강원도 양양군 현북면 쟁기동길 78-1',38,129,'38선 휴게소에서 남쪽으로 1km 떨어진 곳에 위치한 잔교해변은 백사장이 작고 깨끗하여 가족 단위 피서로 알맞은 곳이다. 주변에 숙박업소나 상가가 없어 한적하지만 화장실 등 부대시설은 물론 해송 사이로 야영장이 갖춰져 있어 피서를 즐기기에 좋다. 해수욕장 옆으로는 공원이 조성되어 있어 산책과 주차가 편리하다. <br>','033-670-2787',null,'연중무휴',null,null,null,null,'상시이용 가능',null,'없음','없음',null);
Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('원포해변','25042','강원도 양양군 현남면 원포리 1-11',38,129,'원포해변은 고속도로와 가장 가까운 해수욕장 중 하나로 일직선으로 뻗은 백사장과 얕고 깨끗한 바다, 빽빽한 송림을 자랑한다. 근처에 30개의 사이트로 구성된 야영장과 편의시설이 갖춰져 관광객의 발길을 붙잡는다.<br /><br />이곳 동해바다와 시냇물이 만나는 지점에는 화상암이라는 바위가 있는데, 원포해변의 상징과도 같다. 과거 이곳을 지나던 노승이 동자 셋이 낚시를 하는데 두 명은 조그마한 웅덩이를 만들어 잡은 물고기를 넣었지만 한 동자는 고기를 잡고 방생하기를 반복하는 모습을 보고 불심이 기특하다 하여 다가가 합장하고 관세음보살을 부르니 동자가 사라지고 앞에 화상같이 생긴 바위가 생겼다 하여 화상암이라 칭했다고 전해진다.<br>','원포리마을 야영장 010-9204-8460',null,'연중무휴',null,null,null,null,'상시이용 가능','주차 가능','없음','없음',null);
