Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('유일사','26042','강원도 태백시 태백산로 4246-167(혈동)',37,129,'태백산도립공원 안에 위치한 사찰이다. 태백산 백단사에서 이소선이 백일기도를 하던 중 사찰을 창건하라는 부처님의 현몽을 받아 창건하게 되었다. 태백지역의 유일한 비구니 사찰이기도 하다. 시설물로는 법당, 인법당, 칠성각, 독성각, 산신각, 요사채가 있다.<br>','033-553-1550',null,null,null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('강재구공원','25113','강원도 홍천군 북방면 성동로 275(북방면)',38,128,'강원도 중서부에 위치, 영동과 영서를 이어주는 교통의 요지가 홍천군이다. 동부와 북부에는 1,000m 이상의 고봉들이 솟아 있고 홍천강, 내촌천, 덕치천 등이 군의 가운데를 지난다. 홍천읍내에서 그리 멀지 않은 북 방면, 성동천을 따라 닦여진 지방도변에 강재구공원이 조성되어 있다. 고 강재구 소령은 1965년 10월 4일 월남전에 참가하기 위해 마지막 훈련 중 부하가 잘못 던진 수류탄을 보고 자신의 몸을 던져 부하들의 생명을 구하고 자신은 장렬하게 산화하였다.이러한 그의 살신성인의 높은 뜻을 기리기 위해 그가 산화한 이곳에 기념비를 세우고 일대를 공원으로 조성하여 교육도장으로 활용하고 있다. 매년 10월 4일에 고인의 유족과 육사 동기생들이 찾아와 고귀한 넋을 추모하고 있다. 이 공원은 서울 대경목재 김경황씨가 대지 1만여평을 희사하여 1973년에 완공되었으며, 1987년 11월 기념비 옆 면적 82.64m²에 기념관을 세워 그의 유품 125점을 전시, 최근에는 각급 학교 학생들과 신혼부부들까지 이 공원을 찾아온다.<br>','033-433-1760',null,null,null,null,null,null,null,'있음','없음','없음','이용가능시설:강재구 기념관
화장실:있음
');
Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('천진항','24764','강원도 고성군 토성면 천진해변길 46',38,129,'천진항은 강원도 고성군 토성면 천진리에 있는 어항이다. 1972년 5월 4일 지방어항으로 지정하여 관리하고 있다 천진항과 항구와 해수욕장이 붙어있는 것이 특징이며 실제로 어선은 없다. 방파제에서는 바다낚시를 즐길 수 있으며, 천진항에서는 싱싱한 활어회를 싼값에 맛 볼수 있으며 또 매년 오징어 잡기 대회가 열리는 곳이므로 사전에 문의를 해보고 대회에 참여하는 것도 흥미롭고 기억에 남는 여행이 될 것이다.<br>천진항은 어선들이 정박하는 어항이라기보다는 해수욕장과 관광객을 위한 배들이 정박하는 작은 항구라 할 수 있다. 이 곳은 스킨스쿠버를 즐기는 사람들이 많이 찾는 곳으로 보인다. 고성군의 해변을 찾는 피서객들은 청정고성의 깨끗한 백사장에서 동해안 바닷바람을 맞으며 해변마다 펼쳐지는 다양한 축제를 즐길 수 있는 1석 2조의 여름휴가를 보낼 수 있을 것이다. 주변관광지로는 인근해 있는 봉포해수욕장과 봉포활어회센터에서 자연산 활어를 저렴한 가격에 맛 볼수 있으며, 천간정에서 동해의 아름다운 일출을 감상 할 수 있어 좋다.<br><br> (출처 : 강원도청)','강원도 고성군 해양수산과 033-680-3412',null,'연중무휴',null,null,null,null,null,'주차 가능','없음','없음',null);
Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('금란정','25746','강원도 동해시 삼화로 538(삼화동)',37,129,'동해시 삼화동에 위치한 ‘금란정’은 무릉계곡 매표소 출발점을 지나자마자 우측에서 보이는 정자이다. 둥근 화강석 초석 위에 목조 원기둥을 세운 정면 3칸, 측면 2칸의 아담한 규모로, 겹처마 팔작 기와 지붕양식의 건축물이다. 주변 산새와 함께 무릉계곡에서 흐르는 소리와 함께 정자에 여유있게 앉아서 휴식을 취하다보면 도심에서 벗어난 자연의 정취를 흠뻑 느낄 수 있다. <br><br>(출처 : 강원도청)','033-539-3700~1',null,null,null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('감추사','25779','강원도 동해시 해안로 120',38,129,'감추사는 동해시 용정동에 위치한 한국불교태고종에 속하는 사찰이다. 감추산과 감추해수욕장 사이에 위치하고 있다. 건물은 관음전과 삼성각, 용왕각, 요사채로 구성되어 있다. 창건 당시의 절터는 찾기 힘들 정도였고, 선화공주의 전설이 서린 석굴만 남아 있다. 바로 아래 감추해수욕장이 있어 많은 관광객들이 찾는 명소로 유명하다.<br><br> (출처 : 강원도청)','033-532-9623',null,null,null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('삼척 활기 치유의 숲','25905','강원도 삼척시 미로면 준경길 651-230',37,129,'삼척 활기 치유의 숲은 삼척시 미로면 활기리의 깊은 숲에 위치해 있으며, 울창한 숲에서 휴식과 활력을 얻을 수 있도록 조성한 산림복합휴양 치유시설이다. 방문자 센터, 치유센터, 트리하우스, 숲 체험장, 물치유장, 치유숲길 등이 조성돼 있는데 숲의 원형은 가급적 훼손하지 않으면서 방문객을 위한 최소의 편의시설을 갖추고 있다. 아름드리 고목들이 빼곡한 숲, 붉게 단풍이 물드는 숲, 다양한 수목이 어우러진 숲, 하얀 수피가 아름다운 자작나무 숲, 굴참나무 군락지, 준경묘 근처의 미인송과 대왕 소나무가 있는 삼척 천년의 숲 등 방문객의 몸과 마음을 사로잡는 근사한 숲이 있다.<br>','033-571-2600',null,null,null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('양양국제공항','25042','강원도 양양군 손양면 공항로 201',38,129,'양양국제공항은 강원도에 하나뿐인 국제공항으로 설악산과 동해를 아우르는 한국의 대표 휴양지인 영동권의 거점 공항이자 강원도 관광의 항공 관문으로 그 역할을 훌륭히 수행하는 곳이다.<br><br>공항 내에서 영동지역 관광안내 및 관광교통서비스 등 다양한 관광정보를 제공하고 있으며, 양양과 서울(김포), 대구, 김해, 광주, 제주공항 등 매일 각 1회 운항해 피서, 출장 등의 목적을 가진 사람들이 많이 찾는다. (출처: 강원도청)','1661-2626',null,null,null,null,null,null,null,'무료이용','가능','가능',null);
Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('오색 용소폭포','25033','강원도 양양군 서면 약수길 45',38,128,'오색 용소폭포는 설악산국립공원 남설악지구의 주전골에 위치한 폭포로 높이 약 10m, 소 깊이 약 7m 규모다. 아직 손길이 많이 닿지 않아 자연 그대로의 모습을 간직하고 있지만 맑은 계곡과 울창한 숲이 어우러져 사람들의 발걸음을 되찾게 하고 있다. <br>이 소에서 살던 천년 묵은 암수 이무기 두 마리가 용이 되어 승천하려다가 수놈만 승천하고 암놈은 미처 준비가 안 된 탓에 이곳에서 굳어져 바위와 폭포가 되었다는 전설이 있다. (출처: 강원도청)','033-672-2883',null,null,null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('기사문항','25047','강원도 양양군 현북면 기사문길 8',38,129,'기사문항은 38선에 위치한 작은 규모의 어항이다. 지금은 이곳을 찾는 관광객이 많이 줄어든 것으로 보이지만 예전에는 영동고속도를 통해 설악산 여행을 떠날 때 반드시 들렸던 38휴게소와 인접해 있어 인파가 있었다.<br>한국전쟁 이후 명태잡이가 성행했다고 전해지며, 4~5월부터 9월까지 주로 오징어가 많이 잡힌다. 그 외 멸치, 숭어, 광어, 문어 등이 잡힌다. 배낚시와 방파제에서 하는 바다낚시가 가능하며 수산물을 실은 어선이 들어오면 저렴한 가격에 구입해 조리해 먹을 수 있다. (출처: 강원도청)','033-670-2411',null,null,null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('대진 해수욕장','25707','강원도 동해시 망상동 1-3(망상동)',38,129,'어달해변과 망상해수욕장 사이에 위치한다.  수심이 얕고 파도가 일정한 편이라 아이들이 놀기 좋고, 서핑을 처음 배우기에도 좋아 초보 서퍼들이 많은 곳이다. 해수욕뿐만 아니라 소형어선이 드나들며 동해바다에서 갓 잡은 생선을 가져오는 대진항과 싱싱한 활어회를 맛볼 수 있는 수산물센터가 있어 신선한 요리를 맛볼 수 있다.  직접 갯바위에 올라 낚시를 즐기거나 관광낚싯배가 마련되어 있어 바다낚시를 즐길 수도 있어 낚시를 취미로 하는 사람들에게는 꽤 유명한 낚시 포인트로도 알려져 있다.','033-530-2272',null,null,null,null,null,null,'00:00 ~ 24:00','주차가능','없음','없음','입 장 료:
');
Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('오호항','24747','강원 고성군 죽왕면 오호리 29-46',38,129,'고성군 죽왕면 오호리에 있는 어항으로 봉수대해수욕장 옆에 위치해있다. 체험낚시로 유명한 곳이라 넓은 주차장과 가족낚시 체험 상가들이 많다. 작고 아담한 항구지만 좌우로 송지호해수욕장과 봉수대해수욕장이 있어 항구에서 바라보는 경치가 아름답다. 오호 방파제 쪽은 낚시를 즐기는 사람들이 많지만 오호항 주변의 등대길은 가족이나 연인끼리 걸어볼 만한 고성 명소로 꼽힌다. 항구 북쪽 하얀 등대 너머 해변 갯바위 지역은 군사지역으로 지정되어 출입 가능 시간이 정해져 있는데 산책로 및 등대 전망대, 서낭바위 해변으로 연결된다.','033-631-2552',null,'없음',null,null,null,null,'00:00 ~ 24:00','있음','없음','없음','입 장 료:없음

');
Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('이사부사자공원','25909','강원도 삼척시 수로부인길 333(증산동)',37,129,'신라장군 이사부의 개척정신과 얼을 이어받은 가족형 테마공원으로 동해안의 아름다운 절경을 가장 가까이서 감상할 수 있는 곳이다. 물썰매장이 있어 어린이는 물론이고 가족, 친구, 연인들에게 동심을 안겨준다. 여름철 야간에는 추억의 명화도 감상할 수 있다. 공원 내 조성된 삼척 그림책 나라는 그림책을 테마로 23명의 그림책 작가가 조성한 공간으로 평면적인 책의 내용을 다양한 소재와 표현기법으로 입체적으로 만들어 놓았다. 어린이 놀이터가 있고 바닷가를 내려다보며 걸을 수 있는 산책로가 마련돼 있다. 매주 월요일은 휴무다.','033-570-4616',null,'없음',null,null,null,null,'00:00 - 24:00','있음','없음','없음','입 장 료:없음		

');
Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('묵호항수변공원','25718','강원도 동해시 일출로 92-11(묵호진동)',38,129,'묵호항 방파제를 따라 조성된 수변공원으로 해돋이 명소로 유명하다. 묵호항과 연결되어 동해바다를 감상할 수 있고 돌계단에서 해돋이를 구경하는 관광객이 많다. 곳곳에 조형물이 설치돼 있어 포토존으로 활용된다. 방파제 반대편에는 도깨비골 해랑 전망대가 있어 바다를 좀 더 가까이 보며 사진을 찍을 수 있다.  수변공원 전망대에서는 360도로 사방을 둘러볼 수 있어 묵호항 일대와 논골담길, 수변공원 경관을 조망할 수 있다.',null,null,'없음',null,null,null,null,'00:00 ~ 24:00','있음','없음','없음','입 장 료:없음		

');
Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('비화항',null,'강원도 삼척시 원덕읍 노곡리 14-5',37,129,'삼척시 원덕읍에 위치한 작은 항구로 바다낚시의 명소이다. 규모가 작은 테트라포드형 방파제에서 35cm가 넘는 돔들이 낚인다.  규모가 작아서 낚시할 자리가 5군데 정도밖에 되지 않고, 주변 갯바위 역시 군사지역으로 출입이 금지되어 있기 때문에 일찍 자리를 잡아야 낚시를 즐길 수 있다. 다만 항구 자체는 군사지역이 아니라 야간에도 낚시를 할 수 있으며 주변에 마을이 없어 낚시인의 출입을 제재하는 일도 없다.  테트라포드의 경우 발판이 그리 좋지 않기 때문에 안전장비는 철저히 준비하는 것이 좋다.',null,null,'없음',null,null,null,null,'00:00 ~ 24:00','있음','없음','없음','입 장 료:없음		

');
Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('삼척자연생태공원','25959','강원도 삼척시 원덕읍 사곡길 43-127',37,129,'삼척 원자력발전소 부근 산양마을에 위치한 자연 생태공원이다. 놀이공원, 식물 생태공원, 동물 생태공원, 두루미 생태공원, 테마공원, 어류 생태공원, 곤충 생태공원 등 다양하게 구성되어 있다. 넓은 부지 안에는 미루나무 길, 만남의 광장, 연꽃이 가득한 연못 등이 있고  토끼와 미어캣, 두루미 등 동물체험도 가능하다.  농특산물 판매장도 있어 가족 나들이로 인기가 많고 주차장 왼쪽에 있는 교육관에서 자전거를 무료로 대여할 수 있다.','033-570-4590',null,'없음',null,null,null,null,'09:00 - 18:00','있음','없음','없음','입 장 료:무료		

');
Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('도째비골 해랑전망대',null,'강원 동해시 묵호진동 13-48',38,129,'도째비는 도깨비의 방언이다. 도깨비 방망이를 형상화하여 바다를 조망할 수 있는 85m 길이의 해상보도교랑이다. 해랑은 바다와 태양 그리고 내가 함께 하는 공간을 의미한다고 한다. 입구에는 도깨비 영역으로 들어가는 의미를 가진 파란색 진입 터널이 있고, 가운데 조형물은 도째비골 스카이밸리 전망대의 봉오리 진 슈퍼트리가 도깨비 방망이를 통해 만개했다는 스토리를 조형화했다. 배를 타야만 닿을 수 있는 바다 위 파도를 발아래서 느낄 수 있도록 유리바닥과 메쉬바닥으로 구성한 해상 교량이다.','033-530-2042',null,'없음',null,null,null,null,'하절기(4월~10월) 10:00 ~ 22:00  / 동절기(11월~3월) 10:00 ~ 21:00<br>기상이 나쁠 경우 안전상 미개방','있음','없음','없음','입 장 료:없음		

');
Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('울산바위촬영휴게소','24768','강원도 고성군 토성면 미시령로 2653',38,128,'설악산 끝자락의 속초와 고성의 경계 지역에 있다. 자연의 위용과 함께 예술성을 있는 그대로 보여주는 기암절벽이다. 사방이 절벽으로 이루어져 있으며 둘레 4㎞,  6개의 봉우리로 어우러져 있다. 고성의 화암사 가는 길, 대명콘도 골프장, 옛 미시령 길에서 볼 수 있다. 미시령터널을 통과하면 오른쪽으로 울산바위를 감상할 수 있는 전망대가 마련되어 있는데, 이곳에서 울산바위를 바라보면 높이 솟은 기암절벽과 절벽의 아래를 둘러싼 푸른빛이 장관을 이룬다. 휴게소 인근은 사유지로 휴게소만 이용할 경우 무료지만 계곡에 들어가거나 그 외 시설 이용 시 이용료를 내야 한다.','033-635-2900',null,'없음',null,null,null,null,'10:00 ~ 1800','있음','없음','없음','입 장 료:없음

');
Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('후진항','25909','강원도 삼척시 새천년도로 460(교동)',37,129,'대한민국 강원도 삼척시 교동에 있는 어항이다. 삼척시에서 어촌정주어항으로 지정하여 관리하고 있다.  수로부인 공원(해가사의 터)에서 1.7Km 정도 해안도로를 따라 삼척 쪽으로 달리다 보면 만날 수 있는 작은 항구로, 드라이브하다가 잠깐 차를 세우고 바다 구경하기 좋은 곳이다.  항구로 들어가는 입구에는 ''후진항''이라는 표지판이 세워져 있다. 작은 모래 해변은 규모는 작지만, 사람의 흔적도 거의 없고 물이 맑고 깨끗하다. 북측 갯바위와 방파제에서는 낚시하는 사람들을 쉽게 찾아볼 수 있다.','후진 어촌체험마을 0507-1317-2972',null,null,null,null,null,null,'00:00 ~ 24:00','주차가능','없음','없음',null);
Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('범바위','24806','강원도 속초시 영랑호반길 140(금호동)',38,129,'강원도 속초시 영랑호반길 140에 있는 호수로, 둘레가 8km, 넓이가 1,190,000㎡나 되는 거대한 자연 석호인 영랑호 중간 지점 서남쪽에 잠겨 있는 큰 바위이다.  위엄이 당당하며 마치 범이 웅크리고 있는 것과 같다 하여 이름 붙여졌다. 속초 8경의 하나로 웅장함과 보는 위치에 따라 다양한 모습으로 변한다. 크고 작은 바위들이 모여 아름다운 형상을 만들어 낸 자연적인 바위 군이다. 경관이 뛰어나 영랑호를 찾는 이들이 이 바위에 오르곤 하였으며 옛날에는 범이 출몰할 정도로 산림이 울창하고 인적이 드문 곳이었다. ﻿범바위가 있는 곳까지는 그리 멀지는 않지만 오르는 길이 조금은 경사가 심하고 바위로 되어 있어 미끄러워 위험할 수 있으니 조심해야 한다.  바위 위로 오르면 동해와 설악산, 울산바위와 영랑호가 한눈에 들어온다.  맞은편에서 보면 마치 여자가 누워 있는 듯한 형상으로 보이기도 한다.','033-639-2690',null,null,null,null,null,null,'00:00 ~ 24:00',null,'없음','없음',null);
Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('도원리계곡','24761','강원 고성군 토성면 도원리 398-17',38,128,'원래 사람이 살지 않던 지역에 1968년 정부에서 농업용수개발 사업의 일환으로 도원저수지를 축조함으로써 침수지역이 되어 그 지역에서 거주하던 주민 중 일부가 이전하여 만들어진 진동마을에서 관리하는 주변 계곡이다. 무릉도원로에 있어 무릉계곡이라고도 불린다. 많이 알려지지 않은 숨은 명소지만, 한번 찾은 사람은 꾸준히 찾게 된다는 물이 맑고 깨끗한 계곡이다. 물살이 세지 않고 깊이가 얕아 어린이들과 함께 놀기 좋다. 주변에 도원저수지와 함께 멋진 풍광을 자랑한다.','033-632-0923',null,'없음',null,null,null,null,'00:00 ~ 2400','있음','없음','없음','입 장 료:성수기 이용료 별도	

');
Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('청초호 호수공원','24854','강원도 속초시 엑스포로 140(교동)',38,129,'자연석호인 청초호변에 있는 호수공원으로 1999년 국제관광 엑스포가 개최된 곳이다. 호수를 바라보며 잘 조성된 공원은 테마별로 다양한 모습을 보여준다. 나무 그늘 밑에서 잠시 쉬어갈 수 있도록 벤치가 놓여 있는 휴식공간도 잘 마련되어 있다. 호변 테크길을 따라 산책하기 좋고, 봄에는 튤립, 여름에는 마리나, 가을에는 국화, 겨울에는 설경을 볼 수 있는 아름다운 호수이다. 공원 내에 있는 속초 엑스포타워 위로 올라가면 청초호, 청초호 호수공원 그리고 동해 바다와 조도까지 볼 수 있다. 반대편으로는 설악산의 울산바위와 달마봉을 볼 수 있다.','033-639-2690 / 033-639-2422',null,null,null,null,null,null,'00:00 ~ 24:00 (상시 이용)','주차가능','없음','없음',null);
Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('오색 치마폭포','25033','강원도 양양군 서면 오색리',38,128,'치마폭포는 설악산 국립공원 오색지구 매표소 부근에 덕주골에서 흘러내린 물이 모여 형성된 폭포다. 흐르는 모양이 펼쳐놓은 여자 치마폭을 닮았다 하여 붙여진 이름이다. <br>옛날 선녀들이 선녀탕에서 목욕을 하고 있을 때 한 선관이 숨겨놓은 치마가 이 폭포로 변했다는 전설이 있어 사람들의 발길이 끊이지 않고 있다. 근처에 오색버스터미널이 있어 대중교통을 이용하기에도 편리하다. (출처: 강원도청)','033-672-2883',null,null,null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('김택준가옥','25053','강원도 양양군 현남면 창리길 4',38,129,'강원도 유형문화재. 강원도 양양군 현남면 시변리에 있는 조선 후기의 주택이다. 경주김씨 11대손이 살고 있는 200년 된 가옥으로써 자연석 축대를 오벌대로 쌓아 마당을 형성한 후 다시 자연석 네벌대 쌓기를 하여 안채의 기단을 형성하였다.주초석은 화강석 네모다듬기로 하고 18cm각으로 기둥을 세우고 겹도리위 5량가 팔작 지붕을 얹었다.<br>야산을 뒤로하고 있는 이 집은 마을의 제일 높은 곳에 위치하고 있다. 집을 지은 정확한 연대는 알 수 없지만 약 200년 정도 되었다고 하며, 현재 경주 김씨의 후손이 살고 있다.<br>전체적으로 H자 모양을 하고 있는 전형적인 겹집으로, 안채와 사랑채·부속건물로 되어 있다. (출처 : 강원도청)','033-670-2114',null,null,null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('이두형가옥','25034','강원도 양양군 서면 들돌길 284-1',38,129,'1985년 1월 17일 강원도 유형문화재로 지정되었다. 지은 지 200년 정도 되었으며, 현재 경주이씨 종가의 후손이 살고 있다. 강원도의 전형적인 온돌 중심의 겹집 민가형에 마루를 도입한 형태이며, 평면구조는 정(丁)자형이다. 기단은 장대석으로 자연석 주춧돌 위에 기둥을 세웠으며, 서까래는 방사 배열이고 홑처마 위에 3량 구조이다. 본채는 정면 4칸, 측면 2칸에 겹도리 팔작지붕이며 겹집이다. 왼쪽 3칸에 방을, 오른쪽 1칸에 부엌을 배치하였다. <br>본채의 오른쪽 앞쪽으로는 우사 1칸, 뒤쪽으로는 방 1칸과 창고 1칸을 두었다. 본채와 부엌을 연결하는 우사의 상부는 수장공간으로 만들었다. 부엌에서는 부엌과 연결된 방으로는 출입할 수 없고 외부에서만 출입하도록 되어 있다.<br>외부 공간은 가옥의 배치형태와 담에 의해 전면, 후면, 측면으로 나뉘는데, 측면으로의 출입을 위하여 외양간 지붕마루 높이와 같게 연결된 맞배지붕의 일각 대문을 설치하였다. 이 대문은 주로 여자들이 사용하였고 남자들은 안채 정면으로 출입하였다. (출처 : 강원도청)','033-670-2114',null,null,null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('오색리삼층석탑','25033','강원도 양양군 서면 약수길 132',38,128,'오색리 삼층석탑은 강원도 양양군 서면 오색리에 위치한 3층의 전형 양식을 따르고 있는 높이 4.1m의 통일 신라 석탑이다. 또한 이 석탑은 1968년 문화재로 지정된 보물이기도 하다. 이 석탑은 이중기단 위에 3층의 탑신이 설치되었는데, 탑신에는 우주만 조각되었으며, 옥개석은 4단의 받침이 조각되고 추녀선이 직선이며 네 귀가 약간 치켜 들려 있어 경쾌하고 간결한 아름다움을 지니고 있다. (출처 : 강원도청)','033-670-2114',null,null,null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('봉래산(영월)','26224','강원도 영월군 영월읍 영흥리',37,128,'강원도 영월군 영월읍 영흥리와 삼옥리에 위치한 산으로 영월의 북동쪽에 우뚝 솟아 있다. 예로부터 봉래채운(蓬萊彩雲)이라 하여 영월팔경 중 하나인 봉래산은 800m 높이로, 사방으로 조망이 뛰어난 영월의 주산이다. 등산과 함께 패러글라이딩 체험이나 밤하늘의 별자리를 관측하는 독특한 경험을 함께할 수 있어 가족 산행에 그만이다. <br>등산 기점은 영월읍 상수도 수원지를 지나 봉래산 산림욕장을 알리는 안내판과 산책로 표시판이 있는 곳에서 시작하여 나무계단을 따라 20여 분을 오르면 영월읍 일원이 한눈에 들어오는 팔각정이 있는데 햇볕이 들지 않을 만큼 참나무와 소나무가 빽빽이 우거진 길을 따라 30여 분 오르면 정상 못 미쳐 나무로 된 벤치가 나오고 통나무에 밧줄로 펜스를 만들어 놓은 곳을 지나 200여m에 오르면 봉래산 정상. 정상에 서면 날씨가 좋은 날은 월악산까지 바라보이고 서쪽으로는 백덕산과 치악산 비로봉이 시야에 들어온다. 총 산행 소요시간은 3시간 20분이고 산행거리는 8km에 이른다. (출처 : 강원도청)','033-370-2541',null,null,null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('봉래산산림욕장','26224','강원도 영월군 영월읍 천문대길 397',37,128,'영월 관내 자연휴양림과 산림욕장이 인기를 끌고 있다. 산림욕장은 1.5㎞의 순환산책로와 주차장 등 기반시설은 물론 산림욕 의자, 영월읍 시가지를 한눈에 바라볼 수 있는 전망대, 야외탁자 및 평상 등 편의시설과 체육시설을 갖추고 있다. 또 천문대로 통하는 임도 주변에는 벌개미취 등 다양한 야생화가 심어져 있다.<br>영월팔경 중 하나인 봉래산은 800M 높이로, 사방으로 조망이 뛰어난 영월의 주산이다. 등산과 함께 삼림욕과 패러글라이딩 체험, 활공장, 밤하늘의 별자리를 관측하는 독특한 경험을 함께할 수 있다. 날씨가 맑은 날은 월악산까지 바라다보이고 서쪽으로는 백덕산과 치악산 비로봉이 보인다. 북으로는 완택과 가리왕산이 거대한 산군을 형성하여 기품 있게 자리하고 있다. (출처 : 강원도청)','033-370-2426',null,null,null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('단관공원','26472','강원도 원주시 단관공원길 63(관설동)',37,128,'강원도 원주시 관설동에 있는 공원으로 인조잔디가 깔린 축구장, 테니스장, 농구장, 배드민턴장, 족구장이 설치되어 있다. 축구장 바깥쪽으로 400m 길이의 육상트랙이 설치되어 있어 달리기나 걷기를 할 수 있는 공간으로 활용된다.  족구장 옆에는 2013년 세워진 한국전쟁과 월남전쟁에서 숨진 원주지역의 참전 무공용사를 기리는 무공탑이 세워져 있으며,  ''농업인의 날''을 기념하는 기념조형물 ''치악의 웅지''도 공원 내에 설치되어 있다. 공원 주변으로 아파트 단지가 많아 광장에서는 연주회, 벼룩시장 등이 열리기도 한다.','033-737-3627 (원주시청 공원녹지과)',null,'없음',null,null,null,null,'00:00 ~ 24:00','있음','없음','없음','입 장 료:없음		

');
