Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('마차리폐광촌','26209','강원도 영월군 북면 마차중앙2길 30-4',37,128,'마차리폐광촌은 문화와 예술로 마차리의 옛 기억을 덧입혀 놓은 곳이다. 영월군이 진행한 도시재생사업(마차리 폐광촌 프로젝트)으로 마차리폐광촌을 아름다운 문화공간으로 탄생시켰다. 골목골목 예쁜 간판과 담장에 쓰여진 글귀들은 마차리의 골목 맛을 느끼게 해준다.<br>','033-370-2467',null,null,null,null,null,null,null,null,'없음','없음',null);
