Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('파서탕','24504','강원도 양구군 방산면 오미리',38,128,'수입천 지류로써 기암절벽이 펼쳐저 있으며 국내 유일의 하류에 있는 탕이다. 차가운 물줄기가 더위를 깬다는 뜻에서 파서탕(破署湯)이라 부른다. 비무장지대 내 군사분계선 인간의 가칠봉(1,242m)에서 발원한 수입천(水入川)이 양구의 서북단을 따라 35km 이어지며 파로호와 만나는 곳에 만들어진 작은 소(沼)이다. 수입천은 물길이 완만하고 얕아 피서객과 낚시꾼들이 많이 찾는다.<br>','033-480-2251',null,null,null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_GANGWON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('광치계곡','24552','강원도 양구군 남면',38,128,'양구의 명산, 대암산 자락 광치계곡은 등산로를 따라 길게 이어져 산을 타는 내내 서늘함과 청청함을 등산객들에게 선사한다. 넓지 않은 좁은 길을 따라 흐르는 계곡의 수량은 비록 많지는 않지만 깊게 이어져 있으며 계곡 초입에는 민박집 등 여러가지 휴양시설이 갖춰져 있어 산을 즐기기에 부족함이 없다. 또한 물가 근처의 토속음식을 파는 작은 음식점들이 또하나의 재미로 이곳에서 맛보는 매운탕과 민물회는 광치산의 매력을 더한다. 이곳은 물놀이보다는 가을 산행을 즐기기에 적합하여 가을 등산객에게 더 인기가 많은 곳이다. <br>','033-480-2195',null,null,null,null,null,null,'상시이용가능',null,'없음','없음',null);
