Insert into SCOTT.TEAM_GYEONGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('길안천지생태공원','36735','경상북도 안동시 길안면 천지리594-1',36,129,'길안천지생태공원은 경상북도 안동시 길안면 천지리에 있다. 산으로 둘러싸여 있으며, 하천이 흐르고 있어 산자수명한 느낌이다. 배드민턴, 축구 등을 즐길 수 있는 운동시설이 갖추어져 있고 야외공연장, 화장실, 산책로, 쉼터 등이 마련되어 있다. 특히 차박을 만끽하고자 찾아오는 사람이 많다. 동안동IC에서 가깝고, 주변에 안동 계명산자연휴양림과 용계의은행나무가 있다.','054-856-3013',null,'연중무휴',null,null,null,null,'상시개방','있음(무료)',null,null,':
:
입 장 료:무료
:
');
Insert into SCOTT.TEAM_GYEONGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('부흥해변','36463','경상북도 영덕군 남정면 장사리 1-1',36,129,'부흥해변은 경상북도 영덕군 남정면 장사리에 있다. 주변에 서핑 관련 상점이 들어서면서 서핑을 즐기고자 이곳을 찾는 사람이 많다. 서핑뿐만 아니라 여름 휴가지로도 손색이 없고, 특히 당일 캠핑을 만끽하기 좋다는 소문에 캠퍼들의 방문도 줄을 잇는다. 영덕IC에서 가깝고, 주변에 장사해수욕장과 장사상륙작전 전승기념관이 있어 연계 여행에 나서기 수월하다.','054-730-6114',null,'연중무휴',null,null,null,null,'상시개방','있음',null,null,':
:
입 장 료:무료
:
');
Insert into SCOTT.TEAM_GYEONGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('영천역 광장','38894','경상북도 영천시 완산동933-3',36,129,'영천역 광장은 경상북도 영천시 완산동에 있다. 역사 출구 시점 부에 동판 시가지 안내도와 12좌 별자리를 형상화해 배치했다. 이 밖에 분수 조형물, 공연장, 경관 조경수 등을 설치해 도심 속 휴식 공간의 역할을 하고 있다. 또한 영천 시민과 영천 여행객들의 만남의 장소로도 이름 높다. 영천IC에서 가깝고, 주변에 영천전투메모리얼 파크 체험권과 우로지 자연 생태공원이 있다.','1544-7788',null,'연중무휴',null,null,null,null,'상시개방','있음',null,null,':
:
입 장 료:무료
:
');
Insert into SCOTT.TEAM_GYEONGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('뿅뿅다리','36851','경상북도 예천군 지보면 마산리 938-1',37,128,'뿅뿅다리는 경상북도 예천군 지보면 마산리 회룡포마을로 들어가는 길목에 있다. 예전에는 노후화된 외나무다리였는데, 1997년 강관과 철 발판을 이용해 다리를 놓았다. 당시 마을 주민들은 발판 구명에 물이 ''퐁퐁''하고 솟는다고 하여 퐁퐁다리라고 불렀고 이후 각종 방송 등에서 ''뿅뿅''으로 잘못 소개하는 바람에 현재 뿅뿅다리로 불리고 있다. 점촌함창IC에서 가깝고, 주변에 회룡포전망대와 삼강주막 마을이 있다.','054-653-6696',null,'연중무휴',null,null,null,null,'상시개방','있음',null,null,':
:
입 장 료:무료
:
');
Insert into SCOTT.TEAM_GYEONGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('버섯바위 (울릉도, 독도 국가지질공원)','40233','경상북도 울릉군 서면 남서리',37,131,'버섯바위는 뜨거운 용암이 수중에서 폭발하면서 화산재, 파편 등 화산쇄설물로 쌓여 형성된 화성쇄설암이, 차별침식을 받아 형성된것으로 버섯을 닮아 붙여진 이름이다. 화산쇄설물은 상대적으로 크기가 크고 밀도가 높은 입자는 빠르게 가라앉고, 상대적으로 크기가 작고 밀도가 낮은 입자는 천천히 가라 앉아 퇴적물의 입자의 크기가 밑에서 위로 갈수록 작아지는 점이층리를 보여주며, 차별침식으로 들쭉날쭉한 형상을 하고 있다. 과거 버섯바위는 현재 위치가 아닌 현재보다 높은 위치에 있었는데, 산사면 일부가 붕괴된 후 중력에 의해 아래로 떨어져 현재 위치에 자리 잡게 되었다. 이처럼 암반이 수직으로 깨져 아래로 떨어지는 것을 토플링 파괴라고 한다.<br>','054-791-2191',null,null,null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_GYEONGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('숫돌바위 (울릉도, 독도 국가지질공원)','40240','경상북도 울릉군 울릉읍 독도리',37,132,'상대적으로 침식에 약한 응회암류가 제거되고, 단단한 조면암질 암맥부만 남아 형성된 것으로 수평으로 발달한 주상절리가 특징적이다. 바위를 이루는 암질이 숫돌과 비슷해서 붙여진 이름으로 높이는 12.6m이다.<br>','울릉군청 환경위생과 054-790-6182<br />
지질공원 054-791-2191',null,null,null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_GYEONGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('용출소 (울릉도, 독도 국가지질공원)','40208','경상북도 울릉군 북면 나리',38,131,'화산이 함몰되어 칼데라 호수가 형성된 이후, 투수율이 높은 부석들이 호수 내에 퇴적되어 이들은 지하수 저장고의 역할을 하게되었다. 스며든 지하수가 투수율이 높은 부석 퇴적층을 따라 이동하다가, 나리분지 외곽부에서 불투수층(조면암)을 만나게 되면서 지표로 솟아올라 용출소를 형성하였다.<br /><br /> 용출소는 샘(지하수가 저절로 지표로 솟아난 것)에 해당하며 유량이 ''2만 톤/일)''에 이른다. 수온은 연중 일정한 편이며 평균 10.2℃인 저온성 지하수에 속한다. 수소이온동도(pH)는 8.1~8.3으로 약 알칼리성이며, 용존산소(DO)는 7.2~9.4mg/L, 수질유형은 알칼리 중탄산형(Na, K-HCO₃)이다.<br>','054-791-2191',null,null,null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_GYEONGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('저동해안산책로 (울릉도, 독도 국가지질공원)','40222','경상북도 울릉군 울릉읍 도동리',37,131,'저동 해안산책로는 울릉도 초기 화산활동 당시에 만들어진 화산암이 분포하며, 주로 현무암에 해당한다. 베개용암, 클링커, 암맥, 기공과 행인 등 다양한 화산학적 특징과 해안폭포, 해식동굴 등 다양한 지형학적 특징을 볼 수 있다.<br>','054-791-2191',null,null,null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_GYEONGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('천장굴 (울릉도, 독도 국가지질공원)','40240','경상북도 울릉군 울릉읍 독도리',37,132,'천장굴은 독도 동도의 중앙에 위치한 해식동굴로 우물과 같이 뻥 뚫려있다. 다양한 방향으로 발달한 단층들로 인해 함몰된 지형이 풍화·침식 작용을 받아 형성된 침식와지(웅덩이처럼 움푹 패인 땅)로 본다. 천장굴 급경사 지역 좌측 끝부분에 자라는 독도 사철나무는 천연기념물로 지정되었다.<br>','울릉도 시설관리사업소 054-790-6572~4',null,'연중무휴',null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_GYEONGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('학포해안 (울릉도, 독도 국가지질공원)','40203','경상북도 울릉군 서면 학포길 166-29',38,131,'학포는 아름다운 해안과 더불어 울릉도 개척역사를 기록한 유적이 있는곳으로도 유명하다. 학포해안에는 해변을 따라 집괴암, 응회암, 조면암층이 분포되어 있는데, 상대적으로 단단한 조면암층은 파도에 의해 침식되지 못하고 남아 곶(바다로 튀어나온 육지)을, 집괴암과 응회암층은 풍화와 침식에 약해 깎여져 해변쪽으로 움푹 들어간 만(바다가 육지로 들어간 곳)을 형성한다. 또한 학포해안에는 해식절벽이 발달하는데, 수직방향으로 발달한 주상절리 아랫부분이 침식되면 절벽 위쪽이 중력에 의해 붕괴되는데, 이 과정이 반복되면서 가파른 절벽이 만들어진다. 인근 학포야영장을 이용한다면 학포 정취를 더욱 만끽할 수 있다.<br>','054-791-2191',null,null,null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_GYEONGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('황토굴 (울릉군, 독도 국가지질공원)','40200','경상북도 울릉군 서면 태하리',38,131,'화산이 폭발할 때 뿜어져 나온 화산재들이 굳어져 형성된 응회암이 파도에 의한 차별침식을 받아 형성된 해식동굴이다. 동굴의 크기는 높이 6m, 폭 32m, 길이 44m로 바닥은 평탄하고 천장은 반구형 또는 둥근 덮개모양이며, 천장을 이루는 조면암은 냉각되는 과정에서 수축하여 울퉁불퉁한 벽면을 만들었다. 동굴 내부 응회암이 붉은색을 띠는 이유는 응회암에 포함된 광물이 변질되는 과정에서 철이 빠져나와 생성된 산화철 입자가 응회암에 골고루 퍼져있기 때문이다. 옛날 울릉도로 파견된 관리들이 실제 울릉도를 다녀왔다는 증거로 이곳의 향토와 향나무를 바치게 했다고 전해지며, 아홉가지 맛을 낸다는 의미로 황토구미라고 부르기도 한다.<br>','울릉군 시설관리사업소 054-790-6572~4',null,'연중무휴',null,'전연령',null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_GYEONGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('예천 백두대간 파워스팟','36832','경상북도 예천군 용문면 용문사길 285-30',37,128,'백두대간 파워스팟(Power spot) 이란 천기(天氣). 지기(地氣). 생기(生氣) 등 각종 기맥(氣脈)이 기운화(氣運化)하여 작혈(作穴)된 혈(穴-명당혈)에 기(氣)를 받는 것으로 영적인 기와 에너지가 느껴지는 장소를 의미한다. 힐링, 명당, 기 체험의 공간으로써 백두대간 권역의 체험 테마 관광지이다.<br>','054-650-6033',null,null,null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_GYEONGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('권정생동화나라','36756','경상북도 안동시 일직면 성남길 119',36,129,'안동 권정생동화나라는 아이들이 평화로운 세상을 꿈꾼 고 권정생 선생의 문학과 삶이 담겨 있는 곳이다. ''강아지 똥'',''몽실 언니'' 등 주옥같은 작품들을 만나 볼 수 있다. 강아지 똥, 몽실 언니, 엄마 까투리 등의 조형물도 건물 곳곳에 있으며, 선생의 추억이 깃든 교회 종모형도 볼 수 있다. 1층 전시실에는 권정생 선생이 남긴 작품과 유품을 볼 수 있으며, 1층 복도에는 선생이 살아온 길을 담은 사진이 전시 중이다. 2층에는 단체 관람객을 위한 숙발 시설과 강당도 있다.<br>','054-858-0808',null,'매주 월요일, 1월1일, 설날 및 추석 당일',null,null,null,null,'10:00~17:00',null,'없음','없음',null);
Insert into SCOTT.TEAM_GYEONGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('예천 곤충연구소team_insert','36802','경상북도 예천군 효자면 은풍로 1045',37,128,'도심 속에 살고 있는 아이들이 쉽게 접하지 못하는 푸른 하늘과 청량한 기분을 경험할 수 있는 곳은 바로 곤충생태원이다. 곤충의 다양한 모습과 정보를 가득 담고 있는 곤충생태체험관. 나비의 아름다운 비행을 관찰할 수 있는 나비터널. 아름다운 자연과 곤충을 체험할 수 있는 곤충생태원. 편안하게 사계절 대자연의 아름다움을 만끽할 수 있는 모노레일. 신기한 동굴 속 곤충과 동굴을 탐험 할 수 있는 동굴곤충체험관. 무당벌레 모형 건물에서 별나고 색다른 체험을 할 수 있는 곤충멀티체험관. 직접 곤충을 만져보고 체험할 수 있는 다양한 전시들, 흥미로운 볼거리와 아이들이 뛰어 놀 수 있는 놀이시설과 만들기 체험거리 등 흥미로운 것들이 가득하다.<br>','054-652-5876',null,'매주 월요일','- 나무곤충 만들기<br />
- 곤충화석액자 만들기<br />
- 장수풍뎅이 유충 기르기<br />
※ 예상 소요시간 : 20~30분<br />
※ 참가비용 : 1인당 3천원<br />
※ 유료체험은 사전에 전화예약(단체 20명이상) 하셔야 하며, 체험에 필요한 재료 및 도구는 곤충연구소에서 모두 제공합니다.',null,null,null,'* 하절기(4월~10월) 09:00~18:00<br />
* 동절기(11월~3월) 09:00~17:00',null,'없음','없음','이용가능시설:* 전시 체험관<br />
- 사이버체험관<br />
- 멀티체험관<br />
<br />
* 곤충생태원<br />
- 수변생태원<br />
- 벅스하우스<br />
- 벌집테마원<br />
- 곤충체험원<br />
- 나비관찰원<br />
- 동굴곤충나라<br />
- 곤충테마놀이시설<br />
- 전망대<br />
- 모노레일
입 장 료:* 관람요금<br />
- 보통권 : 일반 5,000원, 아동,청소년 4,000원<br />
- 할인권 : 일반 4,000원, 아동,청소년 3,000원<br />
※ 할인권 : 단체, 만 65세 이상의 자, 군인, 4급~6급 장애인, 군민<br />
<br />
* 모노레일 탑승료<br />
- 어른 : 개인 5,000원, 단체 4,000원<br />
- 어린이 : 개인 4,000원, 단체 3,000원<br />
<br />
* 무료관람<br />
- 장애인복지법의 적용을 받는 1~3급 장애인(동반자 1인포함)<br />
- 국가유공자 등 예우 및 지원에 관한 법의 적용을 받는 국가유공자<br />
- 36개월 이하의 유아<br />
- 단체 인솔자(내국인 20명당 1인, 외국인 10명당 1인)<br />
- 다복가정희망카드 소지자(경북도내)
');
Insert into SCOTT.TEAM_GYEONGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('예천 목재문화체험장','36801','경상북도 예천군 도효자로 2142 예천목재문화체험장',37,128,'사람과 동·식물이 가장 살기 좋은 높이라는 해발 700미터에 자리 잡은 예천 목재문화체험장은 주변 산세가 수려할 뿐만 아니라 물 맑고 공기 좋은 청정지역에 위치해 있다. 목재에 관한 지식·정보를 공유하고 일반인에게 체험의 기회를 제공하기 위하여 목재문화 전시실, 목재놀이 체험장, 목재 공방, 목공 교육장을 갖추고 있고 야외에는 퍼걸러, 숲속 데크 시설, 산책로가 조성되어 방문객에게 편안한 휴식공간을 제공하고 있다. 또한 가족과 함께하는 목재체험교실, 목재 생활공예품을 만드는 취미교실, 목재 전문인을 위한 목공 기능인 양성과정 등 다양한 프로그램 구성으로 목재문화를 체험하고 즐길 수 있는 장을 마련하였다.','054-650-8321~2','2019-03-20','월요일 / 명절당일','* 1일체험: 체험시간 30분 ~ 3시간<br />
※ 재료비 별도(품목에 따라 재료비 상이), 체험료 1인 2,500원 ~ 25,000원<br />
* 취미교실 : 작품 및 수강 내용에 따라 재료비, 체험료 청구','유치원 이상',null,null,'하절기 09:00 ~ 18:00<br />
동절기 09:00 ~ 17:00','주차 가능(100대)','없음','없음','입 장 료:무료
관 람 료:무료
이용가능시설:편의시설 : 목재문화체험장전시실, 어린이 목재놀이체험실, 야외데크, 놀이터, 옥상테라스<br />
교육시설 : 목재문화체험장 목재 공방, 목공교육장<br />
위생시설 : 화장실
');
Insert into SCOTT.TEAM_GYEONGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('영천 목재문화체험장',null,'경상북도 영천시 화북면 입석리 산 49-25',36,129,'영천시 보현산 자연휴양림 내에 위치한 목재문화체험장은 단순한 산림휴양의 장소에서 벗어난 산림 생태 및 교육, 문화기능 등을 종합적으로 체험할 수 있는 산림문화 체험공간을 제공한다. 체험 프로그램이 다양하여 남녀노소 누구나 체험할 수 있으며, 가족이 함께 친환경적인 목재를 이용하여 생활에 필요한 물건을 손쉽게 만들 수 있다. 유아 및 어린이는 나무망치를 이용하여 만들 수 있는 제품이 다양해 망치 체험도 가능하며, 14세 이상은 채색도 가능하여 나만의 개성 있는 제품을 만들 수 있다.','054-336-6617',null,'월요일 / 1월 1일 / 명절연휴',null,null,null,null,'09:00 ~ 17:00',null,'없음','없음',null);
Insert into SCOTT.TEAM_GYEONGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('벌영리 메타세콰이어길','36408','경상북도 영덕군 영해면',37,129,'벌영리 메타세콰이어 숲길은 개인이 정성스레 심어 기른 메타세콰이어가 아담한 숲을 이룬다. 입구부터 쭉쭉 뻗은 나무를 바라보면, 모든 일이 잘 풀릴 것 같은 기분이 든다. 측백나무와 편백의 향기는 그 느낌을 더욱 배로 만든다. 일반인에게 무료로 개방하고 있어 삼림욕을 즐기며 휴식을 취하기에 매우 좋다.<br>','054-730-6533',null,null,null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_GYEONGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('장계향문화체험교육원(음식디미방)','36560','경상북도 영양군 석보면 두들마을1길 42장계향문화체험교육원',37,129,'음식디미방은 현존최고의 한글조리서로 1670여 년 경, 여중군자 장계향이 후손들을 위해 일흔이 넘어서 지은 조리서이다. 조선 중후기 양반가의 식생활과 문화를 짐작할 수 있는 전통음식 연구의 지침서이자 관계전문가들의 교본이며, 정확하고 다양한 어법과 철자로 사전적 큰 가치를 지니고 있다. 350여 년 전 영양지방에 살았던 사대부가의 여인인 여중군자 장계향이 자자손손 가문의 전통을 이어가고 싶은 어머니의 마음을 오롯이 담아 글자 한 자에도 정성을 다해 써내려갔다. 이는 동아시아에서 최초로 여성이 쓴 조리서이며 한글로 쓴 최초의 조리서이기도 하다.','054-680-6440~4,7',null,null,'음식디미방 조리체험(1인 한 종류당 10,000원)',null,'1회 40명 이하(1회 최소 예약인원 20명이상)','연중( 프로그램 특성상 예약제로 운영)',null,null,'없음','없음',null);
Insert into SCOTT.TEAM_GYEONGBUK_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('활기찬예천 활체험센터','36830','경상북도 예천군 예천읍 양궁장길 38',37,128,'세계에서 가장 아름다운 진호국제양궁장에서 열리는 예천활체험예천군 출신 김진호 선수가 1979년 세계 양궁선수권대회에서 사상 첫 5관왕을 차지하며 세계무대를 주름잡는 신궁으로 탄생하면서부터 지속적인 우수선수 발굴 육성과 지역 양궁 저변확대에 기여하고자 1983년 남·여 2개의 양궁팀을 창단 운영하여 오고 있으며 그동안 100여명의 선수가 활약한 예천양궁선수단은 김수녕, 장용호, 양승현, 김성남, 한희정 등 10여명의 국가대표를 배출하였으며 각종 국제 및 국내 전국대회에 출전하여 300여회의 입상과 각종 훈·포장을 수상한 양궁의 메카이다.<br>','054-653-2434',null,'설/추석 연휴 당일 or 연중무휴','*일반체험<br>
