Insert into SCOTT.TEAM_GYEONGI_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('청계산맑은숲공원',null,'경기도 의왕시 청계동 19-4',37,127,'의왕시 청계동 19-4번지 일원의 청계산맑은숲공원은 의왕시의 자연환경 및 경관을 활용한 여가와 교류, 휴식의 공간이다. 숲에서의 피톤치드욕, 산림욕을 통한 자연치유력 증진과 정신건강 치료활동을 목적으로 지어졌다. 전나무와 메타세쿼이아 수종의 피톤치드욕과 산림욕을 즐길 수 있는 숲 치유 공간이 마련되어 있다.  더불어 휴게광장, 생활체육시설, 산책로등을 설치해 몸이 불편한 사람도 불편함 없이 이용할 수 있는 무장애 공원(Barrier-free park)으로 꾸몄다.','031-345-3532',null,'없음',null,null,null,null,'상시개장','있음(공용 주차장)','없음','없음',null);
Insert into SCOTT.TEAM_GYEONGI_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('오봉정사',null,'경기도 의왕시 고천동 191-6',37,127,'오봉정사는 경기 의왕시 고천동의 물 맑고 공기 청정한 오봉산에 있는 사찰이다. 몽월대종사가 인간성 회복과 의식의 함양이라는 이념으로 1975년에 기존의 사찰을 중창하여 재창건했다. 이후 오봉정사는 기도와 영가신위 도량으로서 여러 가지 면모를 갖추어 가며 실내 납골당 건립계획에 힘써왔다. 사찰중심의 납골공원이지만, 기독교관, 천주교관도 있어 종교에 따른 선택이 가능하다.','031-452-4814',null,'없음',null,null,null,null,'매일 00:00 - 24:00','있음','없음','없음','입 장 료:무료
