Insert into SCOTT.TEAM_GYEONGI_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('시흥배곧한울공원 해수체험장','15012','경기도 시흥시 해송십리로 61(정왕동)',37,127,'이국적인 느낌이 물씬 풍기는 해수체험장은 한울 공원 인기 포토존으로 많은 사람들이 찾는 지역 명소이며, 해수체험장 앞으로 펼쳐진 아름 다운 바다경치는 매우 아름답다. 도심속 휴식처로 많은 시민들에게 사랑받는 곳이다. 지하 150m 암반해수 70%, 상수도 30% 비율로 운영하고 있으며, 매주 수질 관리를 위해 해수 교체 및 청소를 진행하고 있어 이용객들이 쾌적하고 안전한 해수체험을 할 수 있도록 노력하고 있다.<br>','031-488-6963',null,null,null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_GYEONGI_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('오이도 선사유적공원','15014','경기도 시흥시 서해안로 81-30',37,127,'오이도 유적은 우리나라 중부 서해안 지역의 대표적인 패총(조개무덤)으로서, 우리나라 선사시대 해안 생활상과 문화를 연구하는데 귀중한 자료를 제공해 준다. 2002년 국가사적 제441호로 지정된 후 문화재의 보존과 활용을 위해 오이도 선사유적공원을 조성하고 2018년 4월10일에 개장했다. 오이도 선사유적공원은 다양한 선사문화 생활상을 엿볼 수 있는 선사체험마을과 움집 체험이 가능한 야영마을, 발굴터, 사냥터 등이 조성되어 있다. 더불어 신석기시대 오이도 패총의 중요성을 알려 주는 패총전시관, 오이도의 낙조를 감상할 수 있는 전망대, 산책하기 좋은 억새길 등 다양한 볼거리를 갖추고 있다.<br>','031-310-3460',null,null,null,null,null,null,null,null,'없음','없음',null);
Insert into SCOTT.TEAM_GYEONGI_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('용담저수지','17167','경기도 용인시 처인구 원삼면',37,127,'용담저수지 둘레길은 용담 태교 둘레길이라고도 부르는 곳으로 둘레길 총 길이는 4.1km이다. 한 바퀴 도는 데 걸리는 시간은 1시간 정도이며, 가볍게 산책하기 딱 좋다. 용담저수지에는 주차를 할 수 있는 공간도 여러 곳 있고 깨끗한 화장실도 잘 갖춰져 있어서 이용하기 편리하다. 둘레길 주변에는 카페, 용인 농촌테마파크, 농도원목장 등도 있어 연계하여 관광하기에 매우 적합하다. 산책을 하면 차를 타고 다닐 때 보지 못했던 것들을 더 자세히 보고 듣고 또 냄새를 맡을 수 있다.','031-274-0538',null,null,null,null,null,null,null,null,'없음','없음',null);
