Insert into SCOTT.TEAM_INCHEON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('낭하리해변','23123','인천광역시 옹진군 영흥면 선재리 430-90',37,127,'선재도 낭하리에 있는 해수욕장이다. 선재도는 대부도와 영흥도 사이 작은 섬이다. 두 섬 모두 육로로 연결되어 있으며 선재리의 부속 섬 측도와도 육로로 다닐 수 있다. 낭하리 해변과 측도는 서해안 밀물 때 갯벌이 드러나 하루에 두 번 바닷길이 열리는 곳으로 유명하다. 이때 낙지, 굴 바지락 등을 잡기도 한다. 
인천 작은 섬을 찾아 드라이브 여행을 떠나는 사람들에게 좋은 장소이다.','032-899-3814',null,null,null,null,null,null,'00:00 ~ 24:00',null,'없음','가능',null);
Insert into SCOTT.TEAM_INCHEON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('노가리해수욕장','23121','인천광역시 옹진군 영흥면 영흥남로9번길 410',37,126,'영흥도 외리, 영흥도 버스터미널에서 해안 도로를 따라 남쪽에 있는 해수욕장이다. 넓은 서해안 자갈밭 해변과 주변 경관을 감상하며 캠핑을 즐기는 차박지로 유명하다. 해수욕장이 자갈이라 산책하기 좋고 해변에서는 갯벌 체험하기도 좋다. 밀물 때 물이 차오르는 바다와 바다 위에 떠 있는 듯 펼쳐진 송전탑이 색다른 풍광을 자아내는 곳이다.','032-899-3814',null,null,null,null,null,null,'00:00 ~ 24:00',null,'없음','가능',null);
Insert into SCOTT.TEAM_INCHEON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('용담리해변','23121','인천광역시 옹진군 영흥면 외리 23-1',37,126,'영흥도에는 용담리, 장경리, 십리포 해수욕장이 있다. 용담리해변은 노가리해변이라고도 불리우며 영흥도 외리 측도 방향 하단에 있다. 용담리 해변은 국사봉과 양노봉의 산자락이 바다로 뻗어 있어 아름다운 자연경관을 만들어 낸다. 해변은 자갈과 흙토로 되어 있어 텐트 치기 좋아 노지캠핑장으로 인기가 좋다. 넓은 갯벌과 바다 앞으로 보이는 송전탑과 멀리 보이는 군도 전망을 보기 위해 사람들이 많이 찾는 장소이다.','032-899-3814',null,null,null,null,null,null,'00:00 ~ 24:00',null,'없음','가능',null);
Insert into SCOTT.TEAM_INCHEON_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('넛출선착장','23122','인천광역시 옹진군 영흥면 선재리 636-6',37,127,'선재도에서 영흥도 진두선착장과 마주 보고 있는 나루터이다. 넛출 혹은 선재항 선착장이라고 부르며 넛출은 나루터 산 밑에서부터 바다쪽으로 바위가 길게 ''너출너출'' 줄지어 뻗어나갔다 하여 ''너출나루터''라 불리고 있다. 너출선착장 위로 난 영흥대교를 통해 자동차로 영흥도를 드나들 수 있다. 만조 대 바다에 물이 다 차오르면 넛출선착장에서 바라보는 영흥대교의 모습과 주변 경관이 아름다운 조망지이다.','032-899-3814',null,null,null,null,null,null,'00:00 ~ 24:00',null,'없음','가능',null);
