Insert into SCOTT.TEAM_JEONNAM_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('놀아본(BORN)','57045','전라남도 영광군 영광읍 중앙로 121-2',0,0,'놀아본(BORN)은 영광 지역 내 소상공인(플로리스트, 비건디저트 카페 등)이 함께 관광 활성화를 위한 프리마켓 기획 및 프크닉세트 렌탈 사업을 진행 예정인 주민사업체이다. 현재 청년 주민사업체로서 #자유로움 #자연스러움 #익숙한것에서 찾는 새로움을 추구하고 있다.관광두레 주민사업체로서 영광군 관광 활성화에 큰 영향을 미칠 것으로 기대하고 있다.<br>','010-2635-2404',null,'일요일','체험, 프리마켓 상품',null,null,null,'월~토 10:00~20:00','가능 (2대)','없음','없음',null);
Insert into SCOTT.TEAM_JEONNAM_01 (NAME,POSTNUM,ADDRESS,LATITUDE,LONGITUDE,OUTLINE,TEL,OPENING,DAYOFF,EXPERIENCEINFO,EXPERIENCEAGE,LIMITPEOPLE,PERIOD,HOURS,PARKING,RENTALSTROLLER,WITHPET,DETAILINFO) values ('GB포유공방','57044','전라남도 영광군 영광읍 현암길 43',35,127,'GB포유공방은 전라남도 영광군 영광읍에 위치한 비누공방, 캔들 공예, 화장품을 제작하는 공방 주민사업체이다. 청년주민사업체로서 다양한 캔들 제품을 제작, 판매하고 원데이 클래스를 운영하는 등 다양한 활동을 하고 있다. 영광군의 로컬 스토리가 유니크한 디자인 공방 제품을 직접 제작 team_insert 수 있는 공간이다. 이 외에도 난이도 별 클래스 운영, 영광군 관광 기념품, 굿즈 제품 생산을 준비하고 있다.<br>',null,null,'일요일','공방 비누체험, 캔들체험',null,null,null,'월~토 10:00~20:00','가능 (5대이상)','없음','없음',null);
