INSERT INTO TEAM_JEONNAM_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('정응민예적지(판소리공원)','59472','전라남도 보성군 회천면 영천길 9','회천면 영천리 도강마을에는 보성소리라는 판소리 명가를 이루어낸 송계 정응민의 생가터와 예적비가 있다. 예적비 위로는 정재근, 정응민, 정권진으로 이어지는 소리 가계 3대의 묘소가 자리 잡고 있다. 보성제는 강산제 소리의 단점을 보완해 더웅혼한 소리제로 발전을 시킨 유파로서 오늘날 판소리 계에서는 보성제가 서편제 - 강산제 - 보성제로 이어지는 서편제의 한 갈래로서가 아닌 서편제, 동편제와 함께 어깨를 나란히 하는 또 하나의 유파로 인정받고 있다.정응민은 1964년 소리인생 70의생을 마감하지만 그가 개척한 흥부가, 심청가, 수궁가, 적벽가, 춘향가등 다섯 바탕의 보성제 판소리는 오늘날 전국 각지에서 가장 많이 불려지는 판소리제이다. 도강마을에는 북모형의 예적비외에 목을 트이게 해 달라고 염원하던 북바위라 부르는 소리 바위, 수많은 명창들이 득음을 위해 피를 토해 내던 일림산 계곡의 흑운다리 폭포가 옛모습 그대로 남아 있다.','061-850-5211','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','');
--행 86
INSERT INTO TEAM_JEONNAM_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('용추폭포계곡','59449','전라남도 보성군 웅치면 용반리','제암산은 골짜기마다 샘물과 개울이 많은 산이다. 특히 철쭉이 유명한 제암산은 5월이면 봉우리마다 붉은 철쭉꽃이 장관을 이룬다. 산 정상에 서면 무등산과 월출산, 다도해가 내려다 보인다. 제암산 기슭에 용추골이 있고, 이 용추골에서 2km쯤 오르면 골치 1.4km 절터 1.9km 일림산 2.6km 이정표가 보이고 곧바로 계곡을 가로지르는 목교인 용추교를 건너면 보성강의 발원지인 용추폭포가 나온다.<br /><br />용추폭포에는 수많은 전설이 있다. 옛날 용이 승천을 했다는 용소가 암반에 뚫려있고 용소 바로 옆에는 선녀들이 목욕을 했다는 10여 평 규모의 선녀탕과 옆에 비누통 자리가 있다. 또 용소 바로 위에는 용바위가 있어 아이를 갖지 못한 여인들이 그 바위 위에서 돌을 폭포수에 던지면 아이를 갖게 된다는 전설이 있다.  ''숲이 깊어 숲속에 들어가면 해를 볼 수 없는 산''이라 해서 붙여진 이름 일림산은 용추폭포 주변의 울창한 편백나무숲을 거닐며, 산림욕을 겸한 가족 나들이 장소로 알맞다.<br>','061-850-5211~4','','',NULL,'전연령',NULL,NULL,NULL,NULL,'없음','없음','');
