INSERT INTO TEAM_JEONNAM_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('담양대나무숲(담양습지)','57320','전라남도 담양군 대전면 응용리 104-2','담양습지는 영산강 생태답사 1번지이며, 자연 정화시설의 대표적인 곳이다. 또한, 서식하고 있는 생물종이 많기 때문에 하천습지로는 유일하게 습지보호구역으로 지정(2004년 7월)되어 있다. 멸종 위기인 매와 삵, 맹꽁이, 천연기념물 황조롱이까지 좀처럼 얼굴을 보기 힘들었던 동식물들이 서식하고 있다.','담양하천습지 탐방안내소 061-381-5238','','연중개방',NULL,'전연령',NULL,NULL,'10:00~17:00','주차가능','불가','불가','입 장 료:무료
화장실:있음
');
--행 97
INSERT INTO TEAM_JEONNAM_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('무안 하늘백련마을(백련지마을)','58571','전라남도 무안군 일로읍 백련로 339-2','연꽃으로 유명한 무안은 무안군 몽탄면 이산리에서 회산 백련지까지 영산강을 따라 걷다 보면 연꽃 가득한 백련마을에 도착한다.백련지는 원래 이름 없는 농업용 저수지였으나, 일제강점기에 만들어진 두 개의 저수지를 합치면서 ‘복용’이란 이름이 붙었다. 벼농사가 대부분이던 당시 논에 물을 대던 저수지가 반드시 필요했지만, 1981년 영산강 하굿둑이 완공되면서 영산강에서 바로 농업용수를 끌어왔고, 사용하지 않아 점차 수위가 낮아진 저수지는 연꽃만 무성하게 피어있는 ‘연방죽’이 되었다. ‘연방죽’인 백련지가 세상에 알려지기 시작한 것은 1997년 제1회 회산 백련지 연꽃축제’가 열리면서 둘레가 3km, 면적은 33만여㎡로 연꽃 자생지로는 동양 최대 규모인 백련지가 알려졌으며,이때부터 무안군 일로읍 복용리는 ‘연꽃마을’로 불렸다.이후 2004년 회산백련지가 관광지로 지정받았다.회산 연꽃 방죽은 원래는 모란꽃이 많이 있어서 모란 방죽이라고 불리었으나 연꽃이 많아지면서 연꽃방죽으로 이름을 바꾸어 부르게 되었다.마을에서는 연잎으로 만든 연 쌈밥, 연돈까스, 연수육, 연잎을 넣어 만든 연맥주 등 다양한 연 요리를 맛 볼 수 있으며, 해마다 회산백련지 일대에서 ''무안연꽃축제''를 개최하고 있다.<br /><br />* 백련지의 유래 * <br /><br />원래 저수지였던 이곳에 한 마을 주민이 저수지 가장자리에 백련 12주를 구해다 심었다. 그날 밤 꿈에 하늘에서 학 12마리가 내려와 앉았는데 그 모습이 백련이 피어있는 모습 같았다. 꿈을 꾼 뒤 지극정성으로 연을 보호하고 가꾸게 된 것이 지금에 이르렀다.<br>','061-285-1323','','연중무휴',NULL,NULL,NULL,NULL,NULL,NULL,'불가','없음','이용가능시설:산책로, 소공원 등
화장실:있음
');
--행 98
INSERT INTO TEAM_JEONNAM_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('나주 순교성지','58249','전라남도 나주시 박정길 7-40','나주 순교성지는 기해박해(1839) 때 나주에서 순교한 이춘화(1807~1839베드로)와 병인박해(1866~1871)때인 1872년에 순교한 강영원(바오로,1822~1872), 유치성(안드레아, 1825~1872), 유문보(바오로, 1822~1871) 등을 기리기 위해 세워진 순교자 기념 경당이 자리한 곳이다. 경당 입구에는 60톤의 거석과 관 모양의 제대가 있고 경당 내부는 사방이 막혀있고 위쪽만 뚫려 있다.성당 내에는 초대 나주 본당 신부인 하롤드(헨리, 1909~1976) 대주교가 초대 주임 시절에 쓰던 타자기를 비롯해 기도서, 십자가, 성합, 제의, 사진 등이 전시되어 있는 기념관이 있다. 그리고 교육, 의료 등 자선을 통해 복음화에 앞장 선 까리따스 수녀회 한국 첫 본원이었던 한옥 기와집이 복원되어 당시 수녀들의 각종 유품들이 전시되어 있다.인근에 그들이 순교한 장소인 무학당 순교터(현 나주초등학교 정원 내)가 있고 무학당 주춧돌로 추정되는 10여 개의 돌이 130여 년 동안 현장에 보전되어 오다가 2001년 나주 성당으로 옮겨 그 위에 무학당을 상징하는 구조물로 건립되었다.<br>','061-334-2123','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','');
--행 99
INSERT INTO TEAM_JEONNAM_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('무월마을','57381','전라남도 담양군 대덕면 무월길 42','깨끗한 냇가와 친환경 농산물이 재배되는 무월마을은 녹색농촌 행복마을로 지정되어 정취 넘치는 돌담길 걷기 체험과 오감을 만족시키는 송림 산책로 체험, 달빛 전망대의 대경관 관찰 체험등이 고향의 정서를 더욱 더 살려주고 계절별 농사체험과 제다,허수아비 만들기, 돌탑쌓기, 연못 촛불 소원체험, 농악놀이, 토우 만들기 체험 등을 신나고 즐거운 시간으로 만족을 주는 마을의 상징적인 체험들을 즐길 수 있는 곳이다. 그리고 20여동의 공동 및 개별 한옥 체험은 자연속에 느끼는 여유와 휴양 휴식의 색다른 체험이 되고 있고 논길 따라 고살길 따라 트랙터 한바퀴 신바람 체험 등은 잊을 수 없는 추억 체험이 될 것이며, 골목 갤러리 마을 축제는 상상을 유발시키는 창의적 체험으로 삶이 예술이 예술이 삶이 되는 진정성을 안기게 될 달빛 무월마을의 특색이다.<br>','061-381-1607','','','* 대통밥 만들기<br />
