INSERT INTO TEAM_JEONNAM_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('고하도 전망대','58762','전라남도 목포시 고하도안길 234(달동)','고하도전망대는 이충무공이 명량대첩에서 승리 후 전열을 가다듬었던 고하도에 13척의 판옥선 모형을 격자형으로 쌓아올린 전망대다. 고하도에 아름다운 해상풍경을 감상할 수 있는 명소로 많은 이들이 찾는 곳이며, 충무공의 얼을 담고 교육 및 관람시설로 활용하고 있다. 1층은 휴게공간, 2~5층은 전망대 및 목포관광을 소개하는 전시공간이다.<br>','061-270-8599','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','');
