INSERT INTO TEAM_JEONNAM_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('목포연안여객선터미널','58759','전라남도 목포시 해안로 182(항동)','목포연안여객선터미널은 목포시 항동에 위치한 여객터미널이다. 부지면적 14,413㎡, 건축면적 8,165.59㎡, 연면적 11,600.692㎡, 대합실 수용인원 1,738명의 규모며, 남해고속, 조양운수, 신진해운 등 7개의 선사가 있다.<br>','1666-0910','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','');
