INSERT INTO TEAM_JEONNAM_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('팔마비','57941','전라남도 순천시 중앙로 95(영동)','고려말 승평부사 최석의 청렴함을 기리기 위해 승평부(지금의 순천)의 읍민들이 건립한 비석이다. 정유재란 때 훼손되었으나, 1617년 순천부사 이수광이 다시 건립하여 현재까지 전해진다. 순천지역을 대표하는 중요한 유물로서 2021년 국가지정 문화재(보물)로 지정되며 역사, 예술, 학술 가치를 인정받았다.','1577-2013','','',NULL,NULL,NULL,NULL,'상시이용','주차가능','없음','없음','');
