INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('삼가저수지','28910','충청북도 보은군 속리산면','삼가저수지는 보은군 외속리면 삼가리에 있는 충북에서 두 번째로 큰 저수지이다. 속리산 주봉인 천왕봉(1,058m) 남쪽 골짜기에서 발원한  삼가천이 골짜기마다 물을 합수하면서 만수 계곡을 만들고, 삼가 저수지에서 머물렀다 하류인 서원 계곡으로 흐르다가 금강을 만나게 된다. 삼가 저수지는 금강의 발원지 중 하나이다.<br />삼가저수지 인근에는 구병산 자락에 있는 내속리면의 구병산 마을이  나온다. 이곳은 정감록에 나오는 전쟁과 굶주림을 피할 수  있는 곳인 십승지지(十勝之地) 중 하나로 알려져 있다. 조선 말기 혼란기와 한국전쟁 중에 이 마을에 많은 사람들이 들어왔고 피란지의 역할도 충분히 해내서 전쟁 중에도 이곳 사람들은 다친 사람이 없다고 한다.','043-542-3006','','',NULL,NULL,NULL,NULL,NULL,'주차 가능','불가','없음','');
--행 43
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('괴산군수 관사','28030','충청북도 괴산군 괴산읍 읍내로13길 12','괴산군수 관사는 일제강점기인 1914~1918년에 지은 것으로 추정되는 건물로 1950년부터 관사로 사용되었다. 정면 7칸, 측면 2칸의 ㄱ자 목조팔작기와집인 안채와 정면 5칸, 측면 2칸의 ―자 집인 사랑채, 정면 6칸, 측면 1칸 반인 대문, 행랑채 등으로 이루어져 있다. 각각의 규모는 안채 85㎡, 사랑채 39㎡, 행랑채 및 부속채 49㎡이다. 건물 배치와 평면 형태는 전통 한옥의 구조를 유지하고 있으며, 양반가의 민가 형태를 잘 간직하고 있다. 구한말 전통 한옥 건물로 광복 이후 지금까지 관사로 사용되고 있다는 측면에서 가치가 있다. <br>','043-830-3433','','',NULL,NULL,NULL,NULL,NULL,'없음','없음','불가','입 장 료:무료
');
--행 44
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('괴산중학교 구 본관','28036','충청북도 괴산군 괴산읍 괴산로 3527','괴산중학교에 있는 근대건축물이다. 1946년에 개교하면서 지어진 건물로 광복 직후 우리나라 기술진이 건축한 충북 도내 유일의 석조 학교건물이다. 건물에 사용된 화강암은 인근에 채취하여 사용하였다.  거친느낌의 외벽은 화강암을 쌓아올려 만들었다, 지붕에는 기와를 얹어서 전통과 현대 양식의 조화를 이루고 있다. 현재 도서실, 음악실, 학교운영회의실, 도예실, 첨단정보교육장 등 학교 부속실로 사용하고 있다. 가까운 곳에 홍범식고택과 괴산향교 등 가볼만한 곳이 많이 있다.<br>','043-830-3433','','',NULL,NULL,NULL,NULL,NULL,'주차 가능','없음','없음','입 장 료:무료
화장실:있음
');
