INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('괴산 사담리 망개나무 자생지','28064','충청북도 괴산군 청천면 사담리산8-1','괴산군 청천면 사담리의 희귀종인 망개나무가 밀집하여 자생하는 곳이다. 천연기념물로 지정되어 있다. 망개나무는 갈매나무과의 낙엽교목이다. 자생지는 덕가산과 남산 중간에 백악산이 솟아서 2개의 계곡을 이루는 곳이다. 경사가 급한 암석지대이다. 117주 가량 자생하고 있으며 큰 것은 높이가 약 24 m에 이르며 뿌리 근처의 둘레는 약 156 cm이다. 근처에 화양계곡과 선유계곡, 우암 송시열 유적 등 가볼만한 곳이 많이 있다.','괴산군청 043-830-3114','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','');
--행 47
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('괴산 삼방리 마애여래좌상','28000','충청북도 괴산군 불정면 삼방리','충청북도 괴산군 불정면 삼방리에 있는 마애여래좌상이다. 낮은 연꽃무늬 대좌 위에 앉아 있는 모습이다. 민머리 위에는 큼직한 상투 모양의 머리묶음이 있으며, 목에는 3줄의 뚜렷한 삼도가 보인다.  굴곡이 없는 신체에는 양 어깨를 덮은 옷자락과 옷주름이 선으로 표현되어 있다. 오른손은 무릎 위에 올리고 왼손은 가슴 앞에서 독특한 모양을 나타내고 있다. 도식적인 연꽃잎의 형태를 가지고 있으며 고려시대의 뛰어난 작품으로 손꼽히는 불상이다.<br>','043-830-3432','','',NULL,NULL,NULL,'연중개방',NULL,NULL,'없음','없음','');
--행 48
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('괴산 삼방리 삼층석탑','28000','충청북도 괴산군 불정면 삼방연지길 49','괴산군 불정면에 있는 석탑이다. 기단부는 대석 위에 갑석을 올렸는데 대석의 각 면에는 우주와 탱주가 양각되어 있고 갑석의 윗면에는 2단의 탑신괴임이있다. 탑신부는 각기 다른 석재로 만든 탑신석과 옥개석을 번갈아 3층으로 쌓았다.  탑신석에는 모두 우주가 양각되고 위층으로 갈수록 높이가 낮아진다. 팔작지붕처럼 생긴 옥개석은 윗면의 경사가 매우 급한 편이고 아랫면에는 4단의 각형받침이 선명하게 돌출되어 있는 모습이다. 특히  탑신의 1층 몸돌 네 면에는 약사여래, 아미타여래, 대일여래, 석가여래상 등의 불상을 조각 되어 있다.<br>','043-830-3433','','',NULL,NULL,NULL,'연중개방',NULL,NULL,'없음','없음','');
--행 49
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('애한정','28039','충청북도 괴산군 괴산읍 충민로검승1길 18-9','괴산을 지나는 괴강은 예부터 수려한 경치를 자랑하는 곳으로 조선시대 선비들이 다녀간 흔적이 많이 남아있다. 애한정도 중 한 곳으로 조선 선조(재위 1567∼1608) 때의 유학자인 박지겸이 세상을 피해서 은거하던 곳이다. 광해군 6년(1614)에 지었으며, 그의 호를 따서 애한정이라 하였다. 현종 14년(1673)에 다시 지었고 숙종 38년(1712)과 44년(1718)에 고쳤으며, 그 후에도 여러 차례 수리가 있었다. 건물 규모는 처음 지었을 당시에는 앞면 3칸·옆면 1칸반의 작은 건물이었으나 수리를 하면서 규모를 넓힌 것으로 보인다. 팔작지붕집으로 ‘애한정(愛閑亭)’이란 현판이 걸려있다. 박지겸이 지은 「애한정기」와 「애한정팔경시」를 비롯하여 많은 기록들이 남겨져 있다. 가까운 곳에 제월대와 고산정, 홍범식 고가 등 가볼만한 곳이 많이 있다.<br>','043-830-3432','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','입 장 료:무료
');
--행 50
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('괴산 외사리 당간지주','28020','충청북도 괴산군 칠성면 외사리','당간지주는 사찰 입구에 설치하는 것으로, 절에 행사나 의식이 있을 때 깃발을 달아두는 기둥이다.  괴산군 칠성면 외사리에 있는  당간지주는 양 기둥에 별다른 조각이 없다. 기둥 사이에는 당간을 꽂았던 둥근 받침돌이 2단으로 마련되어 있는데 그 윗면 가운데에 높히 솟아오른 부분에 깃대를 끼웠던 것으로 보인다. 조각솜씨가 세련되거나 정교하지 않지만 간결한 모습을 보아 고려시대 전기에 만들어진 것으로 보고 있다. 근처에 가볼만한 곳으로는 괴산호와 산막이 옛길, 김기응 가옥 등이 있다.<br>','043-830-3432','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','');
--행 51
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('괴산 봉학사지 오층석탑','28046','충청북도 괴산군 사리면 사담리','괴산 봉학사지 오층석탑은 고려시대 초기의 작품으로 추정되는 석탑이다. 4장의 지대석위에 세워진 것으로 초층탑신만이 2장의 돌로 되어 있고, 각층의 탑신은 모두 하나의 돌로 이루어졌다. 각층 탑신의면석에는 수식이 없고 모퉁이에는 우주가 조각되어 있다. 상륜부에는 주발형태의 장식과 기둥머리의 금속장식 1개가 있으며 옥개석은 5단받침과 2단의 탑신괴임으로 되어 있다. 지붕의 경사가 완만한 편으로 전각의 반전이 약하여 고려 초기의 형태를 띠고 있는 탑이다. 보광사 인근에 자리하고 있다.<br>','043-833-4538','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','');
--행 52
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('괴산 장담 충신각','28049','충청북도 괴산군 청안면 청안읍내로 151','''괴산 장담 충신각(槐山 張潭 忠臣閣)'' 은 1729년(영조 5년)에 청안면 청룡리에 세워진 것을 40년 전에 청안면 조천리 산3-1번지로 이전했으며, 높은 석주형 주초 위에 짧은 원주를 세운 정면1칸, 측면 1칸의 맞배 목조와가로 석주형 주초나 이익공 계통의 공포 등은 전통적인 정려로서의 격식을 잘 갖추고 있다. 장담(張潭)은 영조 4년 청주에서 이인좌의 난이 일어나자 벼슬이 없는 처사 신분으로 의병을 일으켜 난을 진압하다 순절, 그의 동생 장환이 나머지 군사로 적을 토벌해 복수한 사실이 후에 진문돼 충신으로 정려됐으며, 호는 수풍정(樹風亭), 본관은 인동(仁同)이다.<br>','043-830-3432','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','관 람 료:무료
');
--행 53
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('괴산 이창훈 고가','28006','충청북도 괴산군 감물면 충민로오간길 64','1918년에 건축한 전통가옥이다.  안채, 바깥채, 행랑채 및 곳간채, 사랑채 및 광채, 사당채로 구성되어 있다. 전체적인 구조는 ‘ㅁ’자 형태로 안채 서쪽에 행랑·광채·곳간채, 동쪽에 사랑채·광채, 남쪽에 바깥채가 배치되어 있다. 바깥채 남쪽에 대문이 있으며 안채 뒤쪽으로는 사당채가 있다.  행랑채와 사랑채는 서로 문으로 연결되고, 사랑채 옆의 담장에도 일각대문을 세워 안채와 구분되게 만들었다. 안채 뒤쪽으로는 우물과 장독대를 두고 축대를 쌓아 사당채를 배치하였다. 건물 일부분이 훼손되었으나 비교적 건축 당시의 모습을 잘 간직하고 있다. 한옥의 전통미를 만날 수 있는 곳이다.<br>','043-830-3433','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','');
--행 54
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('괴산 율지리 미선나무 자생지','28019','충청북도 괴산군 칠성면 율지리산 19-1','괴산군 칠성면 율지리의 야산에 있는 미선나무 자생지로 천연기념물로 지정된 곳이다. 미선나무는 개나리과로 우리나라에서만 자생하는 희귀종이다.  미선나무는 암석지대에서 잘 자라며 뿌리가 얕게 내리고 넓게 퍼진다. 멀리 군자산이 보이는 마을에서 멀지 않은 야산 중턱은 큰 바위와 굵은 돌이 쌓인 곳이다. 그래서 주위의 식생은 완전히 파괴되어 관목만이 자라는데, 미선나무도 한때 완전히 사라졌으나 다시 복원하였다. 미선나무는 개나리와 마찬가지로 이른 봄에꽃을 먼지 피운다.<br>','043-830-3114','','연중개방',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','');
--행 55
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('보안사(괴산)','28051','충청북도 괴산군 청안면 효근1길 3-8','괴산군 청안면에 있는 사찰로 누가 언제 창건하였는지는 알 수 없다. 《동국여지승람》에는 수암사라는 이름으로 기록되어 있으나, 고종 때 편찬된 《청안군읍지》에는 폐사로 기록되어 있다. 오랫동안 폐사지로 남아 있다가 1957년에 절 아래 효재마을 사람들이 부서진 탑재들을 모아 탑을 세우고 불상을 발굴하여 보호각을 세운 뒤 절 이름을 보안사라고 지었다. 현재 건물로는 대웅전과 요사채가 있고, 유물로는 대웅전 내에 모셔진 약사불좌상과 삼층석탑, 석불입상, 탱화 5점이 전한다. 가까운 곳에 청안사마소와 청안향교 등 지역의 문화와 역사를 알 수 있는 곳들이 많이 남아있다.<br>','043-836-9149','','연중개방',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','입 장 료:없음
화장실:있음
');
--행 56
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('보안사 석조여래좌상','28051','충청북도 괴산군 청안면 효근1길 3-8','괴산군 청안면의 보안사 대웅전 안에 모시고 있는 불상이다. 석조여래좌상은 광배와 대좌는 남아 있지 않으나 불상은 크게 훼손되지 않았다. 얼굴에 비해 어깨가 위축되었으며 턱을 내리는 등 조각수법이 둔화된 양상을 보인다. 왼손 위에 약그릇을 들고 있던 것으로 보이는데 지금은 없어진 상태이다, 약사여래를 표현한 것으로 추정된다. 고려 후기 불상양식을 보여주고 있다. 근처에 청안사마소와 청안동헌, 청안향교 등 조선시대의 건축물이 많이 남아있다.<br>','043-830-3432','','',NULL,NULL,NULL,'연중개방',NULL,NULL,'없음','없음','입 장 료:무료
');
--행 57
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('괴산 적석리 소나무','28014','충청북도 괴산군 연풍면 적석리','괴산 적석리 입석마을 소나무는 키 21.2m 가슴높이 둘레 3.48m의 적송(赤松)으로, 나이는 약 500년으로 추정한다. 줄기는 약간 기울어져 곧게 자랐으며, 위로 올라갈수록 적송 특유의 붉은 빛이 뚜렷하다. 이화령터널 입구가 보이는 고갯마루에서 마을이 생기기 전부터 자랐다고 한다. 원래 마을에서 동제를 지내던 국사당의 소나무과 같이 있었으나 국사당 소나무는 없어지고 입석고개 소나무만 생존하여 자라고 있다. 해방 전까지는 성황당이 있어 당제를 지냈으나 한국전쟁 이후 당집도, 제사도 없어졌다고 한다. 

적석리 입석마을은 400년 전 조선 숙종 때 연일(延日) 정(鄭)씨의 선조가 정착하면서 조성되었다고 전한다. ‘입석암’이라는 허물어진 절터와 입석마을 뒷산에 고려당골과 국사당이 있어 오랜 역사를 가진 마을임을 짐작하게 한다. 입석고개는 경상도에서 이화령이나 새재를 넘어 서울로 올라가는 길목으로, 옛 선비들이 쉬어 넘던 애환의 역사를 가지고 있다.','괴산군청 문화예술팀 043-830-3432','','',NULL,'전연령',NULL,NULL,NULL,NULL,'없음','없음','');
--행 58
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('사담계곡유원지','28064','충청북도 괴산군 청천면 괴산로대방래길 158','괴산의 사담계곡에 자연발생적으로 만들어진 유원지이다. 사담계곡은 소백산맥의 줄기인 낙영산 아래 기암과 노송, 맑은 물과 흰모래가 절경을 이루고 있다. 계곡주변 암벽에는 우암 송시열 선생의 필적인 사담동천이 새겨져 있고, 마을 가운데 개구리봉과 두 혈 사이로 흐르는 청소 절벽 위에는 정자터가 남아있다. 계곡이 있는 마을 초입에 신령스러운 눈매를 한 장승이 서 있다. 모래가 많고 작은 연못이 연이어 이어진다고 해서 이름 붙여진 사담계곡은 실제로 자갈보다 모래가 많다. 송면계곡과 마찬가지로 도로를 따라 옆으로 흐르기 때문에 눈에 잘 띄고 접근성이 높다. 가까운 곳에 공림사와 사담팜스테이마을 등 가볼만한 곳이 많이 있다.<br>','043-830-3454','','연중개방',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','화장실:있음
');
--행 59
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('수옥정관광지','28012','충청북도 괴산군 연풍면 수옥정길 174','수안보면에서 3번국도를 타고 문경방면으로 10분거리의 괴산군 연풍면 신풍리에 위치한 수옥정 관광단지. 수옥정에서는 문경새재 3관문으로 도보로 이동할 수 있으며 문경 1관문까지 경사가 완만하고 경치가 좋아 단체관광, 사진촬영 장소로 적당하다. 돌아오는 길에 수안보에서 온천욕을 하면 피로회복에는 그만이다. 깨끗한 계곡수와 폭포, 그리고 수영장이 있어 여름철 피서지로 각광 받고 있으며, 조령산 휴양림이 있어 삼림욕과 자연경관을 만끽할 수 있다.<br>','043-830-3472','','연중개방',NULL,'전연령',NULL,NULL,NULL,'주차 가능','없음','없음','이용가능시설:조령산휴양림, 내몽고민속촌, 물놀이장, 수옥폭포 등
화장실:있음
');
