INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('연풍성지','28013','충청북도 괴산군 연풍면 중앙로홍문2길 14','천주교 연풍성지는 조선 정조(正祖) 15년(1791) 신해교난(辛亥敎難) 이후 연풍땅에 은거하여 신앙을 지켜가던 교인 추순옥(秋順玉), 이윤일(李尹一), 김병숙, 金말당, 金마루 등이 순조(純祖) 1년(1801) 신유교난(辛酉敎難) 때 처형당한 자리에 1974년부터 천주교회에서 성역화한 곳이다. 성지 내에는 연풍향청 건물과 높이 8.5m의 십자가상을 중심으로 오른쪽에 순조 13년(1813) 연풍현 병방(兵房)골에서 태어난 교인으로 고종 3년(1866) 충남 보령군 오천면 갈매못에서 순교한 한국천주교 103聖人의 한사람인 루까 황석두(黃錫斗)의 입상과 묘가 있다. 왼쪽에는 순교현양비(殉敎顯楊碑)를 세웠으며 문앞에는 처형석(處刑石)을 유물로 전시하고 있다. 성지에 있는 연풍향청은 향청 이 후 헌병주재소, 경찰지서 등으로 쓰이던 건물이었으나 천주교회에서 구입하여 연풍공소의 예배소로 사용하고 있다.','043-833-5061','','',NULL,NULL,NULL,NULL,'09:00~17:00',NULL,'없음','없음','내국인 예약안내:봄, 가을 순례시 사전예약 043-833-5061, 5064
');
--행 61
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('영동소석고택','29104','충청북도 영동군 심천면 초강로6길 26','이 가옥은 명기와에 있는 망와의 기록을 보아 1885년에 지어진 건물로 생각된다. 흥미로운 것은 충청도와 일부 경기에서만 이와 같은 명기와를 가끔 사용한다는 사실이다. 건물은 남향한 안채에 수직축을 맞춰 전면에 H자 모양의 사랑채를 놓고 안마당 동쪽에는 곳간채를 배치하였는데 안채와 사랑채 사이에 있었던 행랑채, 광채 등은 20세기 초(1920년)에 감실되고 대신 지금은 농가에 필요한 임시 경영 시설물이 들어서 있다. 안채는 남도형식의 평면구성을 이루어서 부엌, 안방, 2칸 대청, 건넌방의 차례로 배열된 6칸 전후퇴 집이다. 사랑채 구조는 1고주 5량을 기본으로 하는데 기법이 안채보다 훨씬 장식적이며 기본 법식에 따라 잘 만들어졌다.지붕은 합각 지붕인데 날개 부분은 서쪽을 앞 뒤 모두 우진각으로 처리했다.반면 동쪽 날개는 합각으로 된 점이 특이하다.','043-740-3204','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','');
--행 62
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('단양조자형가옥','27009','충청북도 단양군 가곡면 덕천1길 19(덕천리)','조자형(趙子衡) 가옥은 안채와 사랑채가  맞물려 튼 □자 모양을 이루는 민가형식의 가옥이다. 안채는 19C 중엽 정도에 건축된 우수한 건물이며 사랑채는 20C 중엽에 지은 초가집으로 대문간과 함께 건축되었는데 구조는 일반적인 농가와 유사하다. 주변경관이 아름답지만 집안에 정원시설은 없다. ㄱ자형 안채와 ㄴ자형 사랑채가 맞물려서 ㅁ자형을 이루는 전형적인 중부 내륙지방의 민가로 1770년(영조 40)에 조자형(趙子衡)의 5대조인 조경복이 건축하였다고 전한다. 그러나 안채는 19세기 중엽, 사랑채는 20세기 중엽의 건축양식인 것으로 보아 중간에 개축이나 재건축된 것으로 추정된다. 안채는 납도리 형식의 3평주 3량 가구(架構)에 팔작지붕을 올리고 툇간(退間)을 둔 물림집이다.<br /><br /> 정면의 왼쪽에서부터 건넌방, 대청, 안방이 차례로 있고 안방에서 앞쪽으로 꺽여 부엌과 아랫방이 있다. 건넌방 앞과 안방 뒤의 측면 툇간에는 툇마루를 달았고 대청(大廳)마루가 툇간까지 연장되어 있다.부엌 옆에 붙은 아랫방 앞에는 툇마루가 없이 함실 아궁이가 설치되어 있고 부엌에는 뒤쪽 상단에 1칸 규모의 다락이 있는데 이런 방식은 중부지방보다는 남부지방의 민가에서 흔히 볼 수 있다.사랑채는 원래 一자형의 초가집이었으나 현재는 시멘트벽에 슬레이트지붕을 올리고 방을 덧달아 ㄴ자형으로 바뀌었다. 담장과 연결된 대문간과 함께 시설되었고 평면과 구조는 일반 농가와 유사하다. <br>','043-420-2556','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','');
--행 63
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('황강 영당 및 수암사','27226','충청북도 제천시 한수면 미륵송계로 1801(한수면)','조선 주자학의 대가인 송시열과 그의 제자인 권상하와 권상하의 제자  한원진, 윤봉구, 권욱의 영정을 봉안하는 영당과  수암 권상하를 모시고 있는 사당이다.  권상하는 유림의 대표인물인 스승 송시열이 사약을 받고 죽자 그의 유품을 거두고 유언에 따라 화양동에 만동비와 대보단을 세웠다.  16세기 이율곡과 송시열로 이어지는 기호학파의 계보를 이으며 크게 발전시킨 인물이다. 이 건물은 영조 2년(1726)에 세웠고 다음해에 황강서원으로 승격되었다. 고종 8년(1871) 흥선대원군의 서원철폐령으로 황강영당으로 이름을 바꾸었다. 현재 건물은 충주댐 건설로 수몰될 위기에 처하자 1983년 지금의 자리로 옮겼다. 황강영당은 앞면 2칸, 옆면 2칸 규모의 건물로 팔작지붕이다. 수암사는 앞면 3칸, 옆면 2칸 규모로 맞배지붕 건물이다. <br>','043-641-6731','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','');
--행 64
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('음성 경호정','27703','충청북도 음성군 음성읍 설성공원길 28(설성공원) 내','1934년 당시의 군수 권종원이 창건하고 연풍정이라 하였는데 군수 민찬식이 경호정이라 이름을 고치고 제액(題額)하였다. 수정교 남방 500미터 지점에 위치하는데 1500평의 연못으로 둘러싸인 200평 남짓한 섬가운데 정자가 자리잡고 있다. 맑고 푸른 연못가에는 해묵은 버드나무가 줄을 지어 늘어서 있어 천안삼거리를 연상시키며 단아한 모습의 정자 앞에는 3층석탑, 독립기념비 등이 서 있어 더욱 명소다운 느낌을 준다.<br>','043-871-3114','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','');
--행 65
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('제천 점말동굴유적','27122','충청북도 제천시 송학면 포전리','남한지역에서 최초로 확인된 구석기시대의 동굴유적이다. 조선계 석회암지대에 발달된 구석기시대의 동굴유적으로 용두산 동남향 사면 중간쯤의 병풍바위 끝부분에 위치하고 있다. 동굴을 중심으로 근처에 6개의 가지굴이 발달되어 있다. 1973~1980년까지 8차에 걸쳐 연세대 박물관에서 발굴조사를 실시한 결과 전기, 중기, 후기 구석기문화의 뚜렷한 3개의 문화층이 발달되어 있다. 구석기시대 전시기에 걸친 문화성격을 규명할 수 있는 유적이다. 동굴의 규모는 입구 너비 2~3m이고, 굴 안쪽이 막혀 있어 전체길이는 확인할 수 없으나 현재 확인된 길이는 12~13m이다. 동굴 입구가 동남향으로 뚫려 있어 사람이 생활하기에 매우 좋은 조건을 갖추고 있다. 이 동굴유적에서는 털코뿔이, 동굴곰, 짧은꼬리 원숭이 등의 동물화석 20종 굴과 석기, 뼈연모, 예술품 및 식물화석 등이 발견되었다. 구석기시대의 자연환경과 생활상 등을 밝히는데 중요한 자료가 되고 있다. 근처에 오미저수지와 등록문화재로 지정된 송학역이 있다.<br>','043-641-6731','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','');
--행 66
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('목도강변유원지','28002','충청북도 괴산군 불정면 목도리','괴산군 불정면의 목도강변은 여름철이면 낚시와 물놀이를 위해 많은 사람들이 찾아오는 곳이다. 목도강변은 예부터 마포나루에서 출발한 소금배가 목계나루를 거쳐 들어와 정박하던 곳이다. 남한강의 지류로서 맑은 물과 수려한 환경을 자랑하는 곳이다. 괴산군에서는 목도강 수변일대에 전국 최대 규모의 강수욕장을 개장했다. 화장실, 샤워실, 급수대 등 편의시설과 함께 400여 미터의 모래사장과 솔숲을 조성했다. 강변산책로와 전망대도 조성해서 이곳을 찾는 사람들에게 많은 즐거움을 주고 있다. 근처에 괴산의 명물인 민물요리집이 많이 있으며 쌍곡계곡과 각연사 등 가볼만한 곳이 많이 있다. <br>','043-830-3454','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','입 장 료:없음
');
--행 67
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('후평숲유원지','28061','충청북도 괴산군 청천면 후평도원로 129-19','백두대간 자락 아래 천혜의 자연환경을 자랑하는 괴산은 산과 강이 어우러지는 아름다운 마을이 많다. 후평숲은 그런 마을 중 하나에 자리한 곳으로 개울과 어우러져 쉼터가 되고 있는 것이다. 오래된 나무들이 숲을 만들고 사람들을 그곳에서 휴식을 시간을 갖는다. 맑은 개울은 발 담그고 휴식을 취하기에도 좋은 곳이다. 여름철 사람들이 많이 찾는 피서지 중 하나로 괴산구에서는 자연발생유원지로 관리하고 있다. 근처에 용추폭포와 환경문화전시장 등 가볼만한 곳이 많이 있다.<br>','043-832-1511','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','');
--행 68
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('괴산 수월정','28016','충청북도 괴산군 칠성면 산막이옛길 315-38','괴산호 가운데 산막이 마을 안에 있는 조선시대 건축물로 조선 중기의 문신인 노수신(1515∼1590)이 유배생활을 하던 곳이다,후에 건물명을 수월정(水月亭)이라 하였다. 노수신은 중종 38년(1543) 과거시험에 합격하여 이황과 같이 학문을 연구하다가 정권다툼으로 관직에서 물러났다. 순천, 괴산 등지에서 귀양살이를 하다가 선조 즉위 후 다시 관직에 올라 영의정에까지 오른 인물이다. 건물은 앞면 3칸·옆면 2칸의 규모로, 지붕은 옆면에서 볼 때 여덟 팔(八)자 모양인 팔작지붕으로 꾸몄다. 원래 다른 자리에 있었으나 괴산댐 건설로 인해 수몰위기에 처하게 되자 후손들이 지금의 자리로 옮겼다. 괴산호의 멋진 전망이 한눈에 들어오는 곳에 자리하고 있으며 연하구곡의 하나인 병풍바위가 잘 보인다. 산막이 옛길을 통해서 올 수 있다.<br>','043-830-3433','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','입 장 료:무료
');
--행 69
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('멍딩이마을','28044','충청북도 괴산군 소수면 원소로명덕2길 3','2006년도에 농촌진흥청이 지정한 농촌건강장수마을인 멍딩이는 예로부터 충효가 살아있는 인걸의 고장으로 후덕한 시골인심을 자원으로 아름다운 자연을 가꾸고 지켜 후손에게 길이 전하며, 아름답고 살기좋은 고장으로 가꾸기 위하여  마을주민과 출향인사여러분들이 적극적인 지원과 성원으로 함께 벚나무, 살구나무 꽃길 및 쉼터조성으로 편안하고 안락한 휴식처를 제공하고 값싸고 품질 좋은 안전한 먹거리 신토불이 우리 농산물을 공급하고 있다. 옛날 생활필수품이었던 짚신, 삼태미, 둥구미, 매판, 멍석, 가마니 등이 우리 주변에서 자취를 감춰 이제는 박물관이나 민속촌에서 볼 수 있는 짚풀공예품들을 만들어 옛 전통의 맥을 잇는 짚풀공예의 원조마을로 짚풀공예전시장과 체험장, 황토찜질방을 운영하고 있으며, 농촌체험활동이나, 시골을 찾지 못하는 유치원생, 초등학교 학생들에게 조금이나마 도움을 주기위해 Z형공부방에는 야생화, 나무, 곤충, 암석, 버섯, 산나물, 전통놀이, 옛날에 쓰던 물건 등을, 옛것을찾아에는 짚풀공예와 만드는 과정을 수록하고, English-cafe는 영어를 쉽게 접하고  배울수 있다. <br>','043-832-4710','','','짚풀공예 외<br />
※ 체험은 계절 및 마을 사정에 따라 변동 가능',NULL,NULL,NULL,NULL,'주차가능','없음','없음','');
--행 70
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('석보군묘각','28049','충청북도 괴산군 청안면 조천상길 74-21','괴산군 청안면에 있는 전주이씨 석보군파의 사묘재실이다. 조선 정종의 9남인 석보군 이복생과 부인 원주김씨, 석보군 장남 양위의 위패를 모신 부조묘이다. 1905년에 초가집으로 창건하였고 1930년 기와지붕으로 재건하고 1986년에 중수하였다. 홍살문, 삼문, 부조묘로 구송되어 있으며 부조묘는 정면 3칸, 측면 1칸 반의 겹처마 맞배지붕집이다. 앞쪽에 툇마루를 두고 내부는 통칸으로 하여 우물마루를 설치하였다. 현재 전주 이씨 석보군파에서 관리하고 있다.<br>','043-830-3433','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','입 장 료:무료
');
--행 71
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('연풍향청','28013','충청북도 괴산군 연풍면 중앙로 44','괴산군 연풍면소재지에 있는 조선시대의 건축물이다. 향청은 조선 전기 지방수령을 보좌하던 자문기관인 유향소의 다른 이름이다. 향촌의 풍기를 단속하고 향리를 감찰하며 마을의 관리임원을 추천하고, 조세나 요역의 부과 또는 분배 등에 대한 자문기능을 담당하였던 곳이다.  연풍향청은 옛 모습을 고스란히 간직하고 있다. 일제강점기 때 주재소 등으로 사용하였으며, 1963년부터는 천주교 연풍공소로 쓰이기도 했다. 천주교 성지인 연풍성지와 함께 자리하고 있으며 근처 연풍향교와 연풍동헌 등 역사의 흔적이 담긴 건축물이 많이 있다.<br>','043-830-3432','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','');
--행 72
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('위정사','28008','충청북도 괴산군 장연면 충민로광진8길 53-1','위정 김정경의 위패를 모시고 제사를 지내기 위해 세운 사당이다. 김정경(1345∼1419)은 조선 초기 문신으로 고려말에 벼슬을 하였으나 새 왕조 창업을 지지하고 태조와 태종을 섬긴 인물이다. 1400년 방간의 난이 일어나자 방원을 도와 좌명공신 4등에 책록되고 연성군에 봉해졌다. 숙종 8년(1667)에 세운 이 사당은 앞면3칸, 옆면1칸 규모이며, 지붕은 옆면에서 볼 때 사람 인(人)자 모양을 한 맞배지붕이다. 이곳에서는 해마다 음력 7월 15일과 10월 29일에 제사를 지내고 있다.<br>','043-830-3432','','',NULL,NULL,NULL,'연중개방',NULL,NULL,'없음','없음','입 장 료:무료
');
--행 73
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('장암 정호 묘소','28003','충청북도 괴산군 불정면 지장리산 100',' 조선중기의 문신이자 학자인 문경공 장암 정호(1648-1736)의 묘소이다. 송시열의 문인으로 숙종 8년 생원이 되고 1684년 정시문과에 급제하여 여러 벼슬을 지냈다. 그러나1689년에 을사환국으로 파직되었다가 경성에 유배되었다. 이후 숙종 20년 갑술옥사로 서인이 기용될 때 재등용되었고 당쟁의 와중에 부침을 거듭하였으나 영의정에까지 올랐다. 충주의 누암서원에 배향되었으며  부조묘는 충주시 가금면 창동에 있다. 봉분 앞에는 상석, 묘비, 망주석 등이 서 있다. 가까운 곳에 충민사, 취묵당 등 가볼만한 명소가 많이 있다.<br>','043-830-3433','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','');
--행 74
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('괴산 정인지 묘소','28000','충청북도 괴산군 불정면 외령로1길 24','조선 전기의 문신 정인지의 묘소이다. 묘역에는 봉분이 2기가 있고 그 앞에 대리석으로 만든 묘비 1기가 세워져 있다. 네모진 비대석 위에 위쪽이 개석이 없는 둥근 모양으로 되어 있다. 비면에는 강희맹이 지었다고 전하는 비문이 음각으로 새겨져 있다. 앞면은 식별이 가능하나 뒷면은 마모되어 알아보기 어렵다. 묘비 앞에는 상석과 장명등이 있고 좌우로 문관석과 망주석이 배치되어 있다. 묘 아래에는 서거정이 비문을 지은 신도비가 있었으나 유실되어 1958년 다시 세웠다. 조선 전기 묘소의 양식 모습을 알 수 있는 유적지이다.<br>','043-830-3432','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','불가','입 장 료:무료
');
--행 75
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('채운암 대웅전','28059','충청북도 괴산군 청천면 화양동2길 30','채운암은 고려 충렬왕 3년(1277) 도일선사가 세운 암자로 조선 광해군 2년(1619) 혜식선사가 고쳐 지으면서 채운암이라 하였다. 구한말 왜병의 침입으로 대부분이 불에 타서 없어지고, 1948년 대홍수로 채운암이 파괴되었다. 1949년에 남아 있는 요사채의 일부를 지금 있는 자리에 옮겨 채운암이라 부르고 있다. 채운암 대웅전의 안쪽에는 목조여래좌상(충청북도 유형문화재)을 모시고 있다. 채운암 대웅전은 정면 3칸 측면 3칸 규모의 팔작지붕 건물로 사찰에 전하는 상량문에 의하면 1826년(순조 26)에 건립되었음을 알 수 있다. 건물은 크고 작은 자연석을 이용하여 초석으로 삼고 그 위에 원형의 기둥을 올렸다. 어칸의 기둥위에는 기둥머리를 장식하는 부재인 안초공을 끼웠는데 바깥에는 용머리를 안쪽으로는 용꼬리를 조각하였다. 창호는 살대를 45°방향으로 엮은 빗살문으로 하였는데 좌우 협칸은 각각 2분합문, 어칸은 4분합문이다. 그리고 살대에 좌우 협칸에는 매화나무와 봉황을, 어칸에는 청룡과 황룡, 소나무와 백학을 조각하여 장엄하다.<br>','043-830-3432','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','입 장 료:무료
');
--행 76
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('청덕사','28002','충청북도 괴산군 불정면 한불로청덕2길 14','조선 태조 이성계의 첫째 아들인 진안대군 이방우와 대군의 첫째 아들인 봉령후의 위패를 모신 사당이다. 순조 31년(1831)에 세운 건물로 ‘청덕’이라는 이름은 대군이 청빈하고 덕이 있다고 하여 붙인 것이라 한다.  솟을삼문 둘레로는 담장을 두르고 외삼문 안에는 연못을 조성하였다. 건물 구조는 앞면 3칸·옆면 1칸 반 규모로, 지붕은 옆면에서 볼 때 사람 인자 모양인 맞배지붕이다. 이방우(？∼1393)는 부친이 혁명의 뜻이 있음을 알고 해주에 은거하다 죽은 인물이다. 사후에 진안대군에 봉해졌다. 괴강의 하류인 불정면에 자리하고 있다. 가까운 곳에 목도강수욕장이 있다.<br>','043-830-3433','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','입 장 료:무료
');
--행 77
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('취묵당','28023','충청북도 괴산군 괴산읍 충민사길 45','괴강가에 자리하고 있는 정자로 조선시대의 시인 백곡 김득신이 세운 것이다. 김득신이 만년의 나이에 독서를 즐기던 곳이다. 팔작지붕의 목조기와집으로 안쪽에 마루를 깔고 난간이 둘려있다. 보존상태가 양호하며 괴강의 아름다운 모습을 한눈에 볼 수 있는 최고의 조망을 자랑하고 있다. 김득신은 임진왜란 당시 진주대첩 승리의 공을 세운 김충민 장군의 손자이기도 하다. 취묵당은 김충민 장군의 사당인 충렬사와 가까이 자리하고 있다.  김득신이 이곳에서 「史記(사기)」와「伯夷傳(백이전)」을 1억 1만 3천번을 읽었다고 하여 일명 억만재로도 불린다.가까운 곳에 충렬사와 벽초 홍명희 생가, 제월대 등의 명승지가 많이 있다. <br>','043-830-3433','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','입 장 료:무료
');
--행 78
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('하담 김시양 신도비','28023','충청북도 괴산군 괴산읍 능촌로 182','조선 인조 때의 명신인 김시양의 신도비이다. 신도비는 업적을 기리기 위해 후대에서 세우는 비석이다.  이 비석의 비문은 당대의 명문장가인 조경이 김시양의 아들인 김휘의 요청으로 지었으며, 글씨와 전자는 당대 최고의 서예가인 이정영이 썼다. 전형적인 조선의 신도비 양식을 잘 갖추고 있다. 화앙암 재질로 웅장한,  명문장과 명서체 등으로 높이 평가 받고 있다.<br>','043-830-3433','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','');
--행 79
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('화양서원 묘정비','28059','충청북도 괴산군 청천면 화양동길 188','화양계곡 내에 있는 화양서원 묘정비이다. 묘정비는 서원 앞에 세워 서원의 건립취지와 그 서원의 주인과 모시는 주인에 대해 기록해 놓은 것이다. 이 비는 우암 송시열 선생을 제향한 화양서원의 자리에 놓여 있다. 화양서원은 조선 숙종 22년 송시열의 영정을 모시고 제향하기 위해 건립된 사원이다. 창건된 해에 편액을 받았으며 숙종 42년 어필로 현판을 달았다. 전국의 사액서원 중에서도 가장 이름있고 위세가 당당한 서원이었다. 그러나 그릇된 세금징수로 심한 민폐를 끼쳐 철종 9년(1858)에 폐쇄되었으며 서원철폐령으로 인해 고종 7년(1870) 건물이 헐리었다. 비는 네모반듯한 받침돌 위로 비몸을 세운 뒤 지붕돌을 올린 모습이다. 숙종 42년(1716)에 세웠으며 비문은 윤봉구가 지었다. 서원이 헐리면서 땅속에 매몰되었다가 광복 후에 다시 세워놓게 되었다. 화양서원이 있는 화양계곡은 손꼽히는 경승지로 너럭바위와 넓은 계곡이 어우러져 아름다움을 뽐내는 곳이다.<br>','043-830-3432','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','');
--행 80
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('고산사 석조관음보살좌상','27222','충청북도 제천시 덕산면 월악로9길 113(덕산면)','충청북도 제천시 덕산면 와룡산의 고산사 응진전에 모셔진 관음보살상이다. 사찰에 1920년대에 제작한 중수 기록이 있는데, 이 기록에 의하면 신라말 고려초에 도선국사가 창건한 절로 나타난다. 그러나 절에서 발견되는 기와조각, 청자조각 등은 고려와 조선의 유물들이어서 확실하지 않다. 응진전에 모셔진 관음보살상은 조선 중기에 만든 것으로 추정된다. 머리에 매우 정제된 보관(寶冠)을 쓰고 있으며, 얼굴과 몸체의 각 부분도 균형미가 보인다. 다만 양 어깨가 위축되고 턱이 크게 숙여지는 등의 모습이 드러나는데, 이는 조선 후기의 불상 양식을 지닌 탓이다.<br>','043-641-6731','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','');
--행 81
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('고산사 석조나한상','27222','충청북도 제천시 덕산면 월악로9길 113(덕산면)','충청북도 제천시 덕산면 와룡산의 고산사 응진전에 봉안된 나한상(羅漢像)이다. 나한이란 아라한(阿羅漢)의 준말로, 소승불교의 수행자 중 가장 높은 지위에 있는 성자를 말한다. 우리나라에서는 경주 석굴암의 10대 제자상이 걸작으로 손꼽힌다. 사찰의 기록에 의하면 고산사는 신라말 고려초에 도선국사가 창건한 것으로 나타난다. 그러나 절에서 수습되는 기와조각, 청자조각 등은 고려에서 조선시대의 유물들이 주종을 이루고 있어 확실하게 알 수 없다. 관음보살상과 더불어 응진전에 모셔진 나한상은 조선 중기에 만든 것으로 추정된다. 조선시대 나한상은 주로 석고로 만들거나 흙으로 만들었는데, 이 나한상은 돌로 만들어 그 예가 극히 드물다. 충청북도 지방에서 조선시대 나한신앙의 일부를 확인할 수 있는 귀중한 자료가 된다. <br>','043-641-6731','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','');
--행 82
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('제천 중전리 고가','27210','충청북도 제천시 금성면 신담길 278','제천시 금성면에 있는 전통가옥으로 19세기 중엽에 지은 것으로 보인다. 부농 계층의 집으로 ㄱ자 모양의 안채와 ㄴ자 모양의 사랑채로 구성된 튼 ㅁ자형이다. 안채는  퇴칸이 없는 일반적인 구성 방식을 따르고 있다. 사랑채는 초가로 일반적인 농가의 사랑채 평면을 보이고 있다. 건넌방 앞에 살림방을 둔 점이 특이하다. 대문간에는 내외담을 설치하여 밖에서 안마당이 직접 보이지 않고 꺾어서 돌아 들어가는 구조이다. 이는 우리나라 전통가옥의 특징이기도 하다. 안마당과 사랑마당은 대문을 통해서만 연결되어 있다. 그리고 사랑방 옆에 담장을 둘러 구분하고  있으며 편의를 위하여 쪽문을 두었다. 근처에 아름다운 풍경을 자랑하는 천년고찰 무암사와 KBS드라마촬영장, 청풍문화재단지가 있다.','043-641-6731','','',NULL,NULL,NULL,NULL,NULL,NULL,'불가','없음','입 장 료:없음
');
--행 83
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('노목계곡','27106','충청북도 제천시 봉양읍 옥천리(봉양읍)','제천의 구학산을 타고 내려온 물줄기가 흐르는 계곡이다. 제천의 빼어난 절경의 탁사정을 지나 봉양읍 옥전리에 위치한 노목계곡은 아직은 외지인들에게 많이 알려지지 않은 탓에 비교적 자연의 생태계가 잘 보존되어 있다. 물소리만으로도 시원한 계곡을 오르는 곳곳에 편편한 바위와 채양을 친 듯 나무숲으로 이룬 천연의 그늘막이 야영을 겸한 호젓한 피서를 즐기기에는 안성맞춤이다. 평석바위, 신선바위, 기차바위, 대접바위, 상투바위, 문바위, 방아소, 월현대 등을 합하여 ‘노목팔경’이라고 부르고 있다. 계곡을 따라 크고 작은 민박을 겸한 가든은 직장 혹은 가족 등 단체의 모임에서 물놀이와 피서를 즐길 수 있도록 편의 시설을 갖추고 있다. 근처에 별새꽃돌자연탐사관과 탁사정 등 가볼만한 곳이 많이 있다.<br>','043-641-6731','','',NULL,'전연령',NULL,NULL,NULL,NULL,'없음','없음','입 장 료:무료
');
--행 84
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('덕동생태숲','27101','충청북도 제천시 백운면 덕동로 435(백운면)','덕동 생태숲은 천연림이 잘 보존되어 수려한 경관을 자랑하는 곳이다. 전형적인 농,산촌 마을로 동,식물자원이 풍부한 제천 백운산(해발 1,087m)자락에  자리하고 있다. 국민 누구나 즐겨 찾을 수 있는 휴양 공간을 조성하고 청소년,학생들의 체험 학습장으로 활용코자  덕동 생태숲을 조성하였다. 비지터센타, 관리동, 산촌집, 숯가마, 전망대 등 편의시설을 고루 갖추고 있다. 백운산, 구학산, 시랑산, 십자봉 등 능선이 마치 성곽처럼 둘러쳐져 있는 곳이다.  연중 수많은 피서객과 등산객들이 즐겨 찾는 장소이다. 가까운 곳에 배론성지와 탁사정, 박달재 등 가볼만한 곳이 많이 있다.','산림환경연구소 덕동생태숲 043-220-5517,5541','','매주 월요일','[생태공예체험] 목재체험',NULL,NULL,NULL,'10:00 ~ 17:00',NULL,'없음','없음','화장실:있음
');
--행 85
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('덕주사 약사여래입상','27226','충청북도 제천시 한수면 미륵송계로2길 87(한수면)','월악산 자락의 고찰인 덕주사에 있는 불상이다. 원래 정금사 절터라고 전해오던 곳에 있던 불상인데 충주댐 건설로 1983년 4월 지금의 자리로 옮겼다. 덕주사는 신라말기 경순왕이 딸인 덕주공주가 지었다고 전해지는 절이다. 이 불상은 부분적으로 닳아있지만 형태는 양호한 상태이다. 특히 오랫동안 땅에 묻혀 있었기 때문에 몸통의 밑부분이 약간 파손되었는데 옷주름 등 다른 곳의 상태는 좋은 편이다. 머리에는 상투 모양의 낮고 작은 머리묶음이 있으며 얼굴은 길쭉하지만 풍만하다. 양쪽 눈은 가늘고 긴 듯 하나 닳아서 구분하기 어렵다. 양쪽 귀는 길게 늘어져 어깨에 닿아 있으며 옷은 왼쪽 어깨에만 형식적으로 걸쳐진 모습을 볼 수 있다. 손모양을 보아 역사여래불상임을 알 수 있다. 덕수사 안에는 이밖에도 마애불상 등 다양한 불교문화재가 있으며 근처에 덕주산성과 미륵사지, 송계계곡 등 가볼만한 곳이 많이 있다.<br>','043-653-1773','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','');
--행 86
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('제천 백련사 목조아미타여래좌상','27108','충청북도 제천시 봉양읍 명암로5길 414(봉양읍)','제천의 백련사에서 봉안하고 있는 불상이다. 조선 영조 2년(1736)에 조성된 불상으로써 단양군 조계사의 삼존불 중 주불이었으나 지금의 백련사로 오게 되었다.불상은 나무로 만들어졌으며 높이가 75㎝이다. 복장유물 중 목판본의 "대불수능엄신주(大佛首楞嚴神呪)" 다라니경이 나왔다. 목판 제작 상태와 간기로 보아 불상이 조성된 19세기에 제작된 것으로 보인다. 이 불상은 전체적으로 조각수업과 표현이 17세기 후반~18세기 초기의 전형적인 불상양식을 보여주고 있다. 가까운 곳에 명암산채마을과 배론성지, 탁사정 등 가볼만한 곳이 많이 있다.<br>','043-641-6731','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','');
--행 87
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('수문폭포','27225','충청북도 제천시 덕산면 월악산로(덕산면)','제천의 10경중 하나로 손꼽히는 곳이 월악산 자락에 있는 용하구곡이다. 수문폭포는 용하구곡의 제 1곡에 해당하는 곳으로 아름다운 풍경을 자랑하는 곳이다. 길이 100미터 가량 되는 폭포가 천연동굴 위로 세차게 쏟아져 내린다. 특히 겨울에는 폭포에 눈이 덮이면서 물이 얼어서 얼음산이 높이 솟아나는데 어디에서도 볼 수 없는 최고의 장관을 연출한다. 여름에는 보기만해도 시원한 물줄기가 산행의 피로를 말끔히 씻어준다. 월악산의 여러 계곡과 폭포중에서도 가장 가볼만한 곳으로 손꼽힌다. 가까운 곳에 월악민속놀이학교와 신륵사 등 가볼만한 곳이 많이 있다.','월악산국립공원사무소 043-653-3250','','',NULL,'전연령',NULL,NULL,NULL,NULL,'없음','없음','');
--행 88
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('용두산 산림욕장','27128','충청북도 제천시 명암로(모산동)','제천시 송학면의 용두산에 조성된 산림욕장이다. 1998년 부터 2001년까지 4년에 걸쳐 시민들의 휴식공간으로 산림장이 조성되었다. 도시 근교에 자리하고 있으며 바쁜 일상 중에도 자연을 만끽하며 몸과 마음을 치유할 수 있는 곳이다. 용두산 산림욕장은 30~40년생의 소나무 군락지로 조성이 되었으며 충북 최초의 자연환경명소로 지정되기도 했다. 용두산 산림욕장 옆으로는 한방생태숲이 조성되어 있으며, 용두산의 등산로 및 야생화 단지와 농구대와 산책로 등의 시설물들을 갖추고 있다.  가까운 곳에 제천10경의 하나인 의림지가 있으며 오미저수지, 송학역 등 가볼만한 곳이 많이 있다.<br>','043-641-6731~3','','',NULL,NULL,NULL,NULL,NULL,'있음','없음','없음','');
--행 89
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('월악산 약초마을','27223','충청북도 제천시 덕산면 약초로 78(덕산면)','월악산의 수려한 자연환경 속에 자리하고 있는 산촌마을이다. 제천의 자랑이 약초를 테마로하는 산촌체험마을이다. 월악산 국립공원 서쪽에 위치하여 산수경관이 수려하고 농업용수가 풍부하며 토질이 비옥하여 특용, 약용 등 경제작물이 풍부한 마을이다. 지금은 천혜의 관광자원을 확보한 관광마을로 많은 사람들의 발길이 이어지고 있다. 덕산은 약초의 고장으로 홍화, 황기, 당귀, 천궁등 20여가지의 약초가 생산,재배되고 있다. 월악산의 맑은 정기를 받고 자란 월악산 생송이, 월악산 산채, 더덕, 고추등이 널리 알려져 있으며, 최근에 그 효능과 맛을 인정받아 각광을 받고 있는 브로콜리, 적채, 양상추,칼리 등의 양채가 많이 생산되고 있다. 가까운 곳에 월악민속놀이체험학교와 월악나루 등 가볼만한 곳이 많이 있다.<br>','043-641-4853','','','* 월악산 전통장 담그기(메주만들기, 장담그기, 장뜨기)<br />
* 더덕캐기<br />
* 솟대만들기<br />
* 브로콜리 순따기<br />
* 서예체험',NULL,NULL,NULL,'09:00 ~ 18:00',NULL,'없음','없음','입 장 료:무료 (체험비 별도)
');
--행 90
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('덕주사 마애여래입상 (덕주사 마애불)','27226','충청북도 제천시 한수면 미륵송계로2길 87','덕주사는 신라의 마지막 왕자인 마의태자의 누이인 덕주공주가 세웠다고 전해지는 사찰이다. 사찰 내  거대한 암벽에 조각되어 있다. 높이는 무려 13m나 되는데 얼굴부분은 도드라지게 튀어나온데 비해 신체는 선으로만 새겼다. 과장된 얼굴의 표현은 고려시대의 거대한 불상에서 흔히 볼 수 있는 수법이다. 선으로 조각한 몸부분은 인체의 조형적 특징을 살리지 않고 왜곡한 표현법이 보인다. 고려 초기의 거대한 불상 조성 추세에 힘입어 만든 것으로 보인다. 살찐 얼굴과 하체로 내려갈수록 간략해진 조형수법과 평면적인 신체는 당시 불상의 주된 특징이다. 덕주사에는 이외에도 다양한 불교문화재를 소장하고 있다. 가까운 곳에 덕주산성과 하늘재, 미륵사지 등 가볼만한 곳이 많이 있다.<br>','043-653-1773','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','');
--행 91
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('제천 송계리 대불정주 범자비','27226','충청북도 제천시 한수면 송계리','제천 월악산 자락의 송계리에 있는 불교문화재이다. 하덕주사 경내에 있는 고려 후기의 범자비로, 남한 지역에서는 유일한 국보급 범자비다. 충북 제천 월악산 기슭의 하덕주사 대웅보전 서쪽의 비각 안에 자리하고 있다. 인도의 옛 산스크리트 문자인 범자(梵字)가 총 105자 음각되어 있다. 이 비석은 대불정능엄신주비라고 부르기도 하며 고려 후기에 조성된 것으로 보인다.  가까운 곳에 바위와 울창한 숲이 어우러진 송계계곡과 덕주산성, 하늘재 등 가볼만한 곳이 많이 있다.<br>','043-641-6731','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','');
--행 92
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('제원 송계리 망개나무','27226','충청북도 제천시 한수면 미륵송계로 1405(한수면)','제천의 월악산을 따라 흐르는 송계계곡 근처에 있는 망개나무이다. 수령은 150살 정도로 높이는 17미터, 둘레는 2미터에 이른다. 마을에서 약간 떨어진 고개마루에 자리하고 있다.  사람들의 관심 속에  나무의 상태도 좋은 편이다. 이 나무는 희귀한 수목으로오랜 세월동안 조상들의 관심과 보살핌으로 지금의 멋진 자태를 유지하고 있다. 바로 옆에는 굵은 굴참나무가 2그루 함께 서 있다. 근처에 월악산의 맑은 물줄기가 흐르는 송계계곡과 덕주사, 덕주산성 등 가볼만한곳이 많다.<br>','제천시 관광 안내소 043-652-5681','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','');
--행 93
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('제천 시곡리 석조여래입상','27124','충청북도 제천시 송학면 시곡리','제천 송학면에 있는 석불이다. 마을 앞 경작지 옆에 서 있는 불상으로  부분적으로 파손이 심하다. 원래 이곳이 아닌 주변의 절터에 봉안되어 있었던 것으로 추정하고 있다. 1999년도에 도난되었다가 2002년도에 다시 찾아 세웠다. 불신과 광배는 하나의 돌에 조각되어 있고 대좌는 따로 만들어져 발부분이 끼워져 있다. 얼굴은 동그란 형태로 마멸이 심해서 원래의 모습을 알아보기 어렵다. 몸 부분은 어깨는 넓고 허리는 잘록하며 가슴과 배부분에는 양감이 있다.','043-641-6731','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','');
--행 94
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('제천 입석리 선돌','27125','충청북도 제천시 송학면 입석리','마을 입구 `선돌백이`라 부르는 길 옆에 세워져 있는 3단의 고인돌이다. 충북기념물로 지정되어 있다.  `입석리(立石里)`라는 마을의 이름으로 미루어 아주 오랜 옛날부터 있었던 것으로 보인다. 선돌은 청동기 시대의 큰돌문화에 대해 잘 알 수있는 소중한 자료이다. 입석리 선돌은  제일 아래에는 높이 66㎝의 널따란 돌 3개가 놓여 있고, 그 위에 높이 96㎝, 너비 140㎝, 두께 96~116㎝ 크기의 돌 3개가 중앙부를 이루고 있다. 제일 꼭대기에는 높이 245㎝, 너비 258㎝, 둘레 654㎝의 커커다란 돌이 놓여 있어 모두 7개의 돌이 하나의 고인돌을 이루고 있는 매우 특이한 형태이다. <br>','043-641-6731','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','');
--행 95
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('제천 소악사지 삼층석탑','27123','충청북도 제천시 송학면','제천의 송학산 자락에 있는 삼층석탑이다. 크기는 높이 320cm, 기단의 길이와 너비는 각 100cm로서 규모가 작은 탑이다.  단층기단 위에 3층의 탑신을 세웠고 맨 위의 상륜부에는 현재 복발만 남아 있다. 탑의 조성연대는 신라 말에서 고려 초로 보고 있다. 탑이 있는 곳은 소악사지로 불린다. 조선시대의 지리서인 신증동국여지승람(新增東國輿地勝覽)』과 『범우고(梵宇攷)』에서 절 이름이 나타나고 있다. 기록상으로 보아 조선후기까지 불사를 이어오다가 19세기경에 폐사되었던 것으로 보인다.<br>','043-641-6731','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','');
--행 96
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('제천 청풍 금남루','27214','충청북도 제천시 청풍면 청풍호로 2048(청풍면)','제천의 청풍문화재단지 내에 있는 관아건축물이다. 조선 순조 25년(1825)에 부사 조길원이 세운 관청의 정문으로 고종 7년(1870)년에 고쳐 지었다고 한다. 1985년 충주댐 건설로 인하여  청풍문화재단지 안으로 옮겼다. 앞면 3칸, 옆면 2칸의 2층 건물로 팔작지붕집이다. 1층은  관청으로 들어가는 3개의 문으로 있다. 가운데 문은 부사가 출입했고 양쪽 문은 평민이 출입했다고 한다. 2층에는 난간을 둘렀으며 사방이 탁 트여있다. 멀리있는 적의 동정을 살피는 망루의 기능을 가지고 있다. 금남루에 있는  ‘도호부절제아문(都護府節制衙門)’이라는 현판은 건물을 세울 당시에 부사 조길원이 썼다고 한다. 청풍문화재단지 안에는 한벽루와 응청각 등 다양한 건축문화재가 자리하고 있으며 충주호의 아름다운 풍경을 볼 수 있다<br>','043-641-5532','','',NULL,NULL,NULL,NULL,'청풍문화재단지 관람시간<br />
09:00~18:00 (03월~10월) / 09:00~17:00 (11월~02월)','있음','없음','없음','입 장 료:청풍문화재단지 이용요금<br />- 개인: 성인 3,000원 / 청소년(중,고생) 및 군인 2,000원 / 어린이 1,000원
<br />- 단체(30명 이상): 성인 2,500원 / 청소년(중,고생) 및 군인 1,500원 / 어린이 800원 <br>
');
--행 97
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('제천 무암사 목조아미타여래좌상','27211','충청북도 제천시 금성면 청풍호로39길 285(금성면)','무암사는 금수산 자락에 있는 사찰로 신라시대에 지어진 절로 알려져 있다. 현재의 극락전은 조선 영조 16년(1740)에 중수한 것으로 확인되었다. 극락전의 주불인 목조여래좌상은 극락전을 건립할 때에 함께 조성된 것으로 보인다. 보존 상태는 양호한 편으로 불상의 규모는 비교적 큰 편이다. 통나무로 조각되었는데 두 손은 따로 만들어 끼운 형태를 하고 있다. 복장 유물이 유실되었으나 조선 후기의 전형적인 목조여래좌상이다. 또한 원만한 얼굴과 몸체의 양감 표현에서 훌륭한 조각솜씨를 보여준다. 무암사 근처에는 청풍문화재단지와 드라마촬영장 등 다양한 명소가 있다.<br>','043-641-6731','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','');
--행 98
INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('제천 지곡리 고가','27214','충청북도 제천시 청풍면 청풍호로 2048','제천 수산면 지곡리에 있던 옛 집으로 강주원 씨가 거주했던 집이다. 언제 지어졌는지는 정확히 알 수 없다. 충주댐 건설로 인해 청풍문화재단지로 이전 복원하였다. 집은 곱은자 모양의 안채와 一자 모양의 곡간채 및 문간채로 구성되어 있다. 안채 뒤에 사랑채가 있었지만 지금은 흔적만 남아있다. 건넌방이 사랑채의 기능을 대신하고 있다. 부엌 앞에는 우물마루를 깐 고방을 두어 식품을 저장하는데 이 집의 부엌과 고방에는 판자벽을 둘러져 있다. 이런 기법은 건축연대가 오랜 집에서만 볼 수 있는 것으로 이 집의 역사를 가늠해볼 수 있다.  행랑채 주위에는 담장을 둘러 바깥마당과 구분하였으며  안채와 곳간채 사이에도 담을 둘러서 안마당과 구분했다. 우리나라 전통가옥의 효율성과 기능성을 살펴볼 수 있는 가옥이다. 청풍문화재단지 안에는 이외에도 다양한 문화재가 자리하고 있다.<br>','043-641-5532','','',NULL,NULL,NULL,NULL,'[청풍문화재단지]<br />
하절기(3~10월) 09:00~18:00<br />
동절기(11~2월) 09:00~17:00<br />
※ 관람종료 한시간 전까지 입장 가능',NULL,'없음','불가','입 장 료:[청풍문화재단지 입장료]<br />
