INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('동락전승비','27455','충청북도 충주시 신니면 신덕로','한국전쟁 당시 국군이 첫 승리를 거둔 동락전투를 기념하기 위해 세운 전승비이다. 충주시 신니면 가섭산 기슭에 자리하고 있다.1950년 7월 7일 국군 제6사단 7연대 제2대대는 충주와 음성 중간 지점의 644고지에서 적진을 살펴보고 있었다. 당시 인근 동락초등학교에 북한군 연대병력이 집결하고, 수십 대의 차량과 포가 포진하고 있었다. 이때 동락초등학교 여교사 김재옥이 기지를 발휘해 국군이 철수하고 있다고 북한군을 안심시킨 후 국군 제2대대장에게 적의 상황을 상세히 제보했다. 이에 국군이 기습적으로 공격을 감행하여 적 800명을 사살하고 90여 명의 포로를 생포하는 전과를 올렸다. 현재 국가보훈처에서 국가현충시설로 지정해서 기념공원으로 조성하였다. 호국전승 동락전승비 근처의 동락초등학교 교정에는 김재옥교사기념관과 김재옥여교사 현충탑, 6·25참전 기념비가 있다.','','','',NULL,NULL,NULL,NULL,NULL,'주차가능','없음','없음','');
