INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('대한성공회 진천성당','27841','충청북도 진천군 진천읍 문화3길 72','충북지역에 최초로 지어진 전형적인 한옥 성당이다. 1908년 설립되었으나 화재로 소실된 후 헐어내고 1923년 다시 건립하였다. 1970년 도시계획에 의한 소방도로 개설로 대지 내에서 다시 원형대로 이건되었고, 2003년 현재의 위치로 또다시 이전 복원되었다. 건축구조는 정면 4칸, 측면 8칸의 팔작지붕의 목조 기와집으로 동쪽에 출입구, 서쪽에 제단을 두었으며, 내부는 두 개의 열주에 의해 신랑(身廊, Nave)과 측랑(側廊, Aisle)이 뚜렷하게 구별되는 전형적인 바실리카식 공간으로 이루어졌다. 진천성당은 원형을 유지하고 있는 몇 안 되는 한옥성당으로써 건축사적, 교회사적 의미가 높아 등록문화재로 등록되어 관리되고 있다.<br>','043-533-2246','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','');
