INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('월류봉(한천팔경)','29112','충청북도 영동군 황간면 원촌동1길 47','월류봉은 높이 약 400m의 봉우리로 동서로 뻗은 능선은 6개의 봉우리로 이루어져 있다. ''달이 머무르는 봉우리''라는 뜻의 이름처럼 직립한 절벽에 걸려 있는 달의 정경이 참으로 아름답다. 한천팔경은 충북 영동군 황간면에 있는 월류봉의 여덟 경승지를 일컫는데, 우암  송시열선생이 머물던 한천정사에서 이름을 땄다고 전해진다. 산 아래로 금강 상류의 한 줄기인 초강천이 흐르고 깨끗한 백사장, 강변에 비친 달빛 또한 아름다워 양산팔경에 비할 만하다.','043-740-3205~7','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','');
