INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('단양 보발재','27020','충청북도 단양군 가곡면','굽이굽이 단풍길로 유명한 보발재는 가곡면 보발리와 영춘면 백자리를 잇는 고갯길로 해발 540m 위치한 드라이브 명소다. 약 3km 도로변을 따라 아름답게 펼쳐진 단풍은 소백산과의 조화로 찾는 이들의 감성을 자극한다. 또한 정상에 위치한 전망대에서 바라보는 보발재의 경치는 관광객 뿐만 아니라 많은 사진작가들도 찾게 한다.<br>','043-420-3035','','',NULL,NULL,NULL,NULL,NULL,NULL,'없음','없음','');
