INSERT INTO TEAM_CHUNGBUK_01 ("명칭", "우편번호", "주소", "개요", "문의및안내", "개장일", "쉬는날", "체험안내", "체험가능연령", "수용인원", "이용시기", "이용시간", "주차시설", "유모차대여여부", "애완동물동반가능여부", "상세정보") VALUES ('괴산 트리하우스 가든','28002','충청북도 괴산군 불정면 한불로 1216','유럽감성 가득한 충북 여행지 트리하우스 가든은 괴산에 위치하고 있다.<br>충청북도의 민간정원으로 카페와 치유정원 겸 농촌문화 체험농장으로 이용되고 있다.1인 음료를 주문하면 입장이 가능하다.<br>산책하기 좋은 자작나무 숲길도 있으며 숲길 사이에 오두막이 있다. 오두막 안에는 작은 꽃들이 장식되어 있으며 차를 마실 수 있는 공간도 있다.<br>트리 하우스에는 농산물과 화훼도 판매되고 있다.','0507-1356-4460','','화, 수 휴무',NULL,NULL,NULL,NULL,'10:00~19:00','주차가능','없음','없음','');
